    useRecipeFilter(recipes);
  const { handleFocus, handleBlur, key } = useSearchFocus();
  const { setDebugData } = useDebugContext();
  const { showPopover, handleOptionSelect, popoverVisible, dismissPopover } =
    usePhotoImport();

