  1→import { useRecipes } from "@/features/recipes-list/context/RecipesContext";
  2→import { useSources } from "@/features/sources/context/SourcesContext";
  3→import { Alert } from "@/lib/alert";
  4→import type { SourceId } from "@/lib/types/primitives";
  5→import type { Source } from "@/lib/types/source";
  6→import { isUrl } from "@/lib/utils/recipeValidation";
  7→import { LiquidGlassButton, LiquidGlassInput } from "@/modules/liquid-glass";
  8→import { useTranslation } from "@/platform/i18n/useTranslation";
  9→import { getColors } from "@/platform/theme/glassStyles";
 10→import { useTheme } from "@/platform/theme/useTheme";
 11→import { MaterialIcons } from "@expo/vector-icons";
 12→import * as Haptics from "expo-haptics";
 13→import { router } from "expo-router";
 14→import { useCallback, useEffect, useMemo, useRef, useState, type JSX } from "react";
 15→import {
 16→  KeyboardAvoidingView,
 17→  LayoutAnimation,
 18→  Platform,
 19→  Pressable,
 20→  ScrollView,
 21→  StyleSheet,
 22→  Text,
 23→  TextInput,
 24→  View,
 25→} from "react-native";
 26→import { Gesture, GestureDetector } from "react-native-gesture-handler";
 27→import Animated, {
 28→  type SharedValue,
 29→  interpolate,
 30→  interpolateColor,
 31→  runOnJS,
 32→  useAnimatedStyle,
 33→  useSharedValue,
 34→  withSpring,
 35→} from "react-native-reanimated";
 36→import { useSafeAreaInsets } from "react-native-safe-area-context";
 37→
 38→function useActionButtonScale(
 39→  translateX: SharedValue<number>,
 40→  sign: 1 | -1
 41→): { transform: Array<{ scale: number }>; opacity: number } {
 42→  return useAnimatedStyle(() => {
 43→    const w = Math.max(sign * translateX.value - BUTTON_GAP, 0);
 44→    if (w <= SCALE_MIN_WIDTH) {
 45→      return { transform: [{ scale: 0 }], opacity: 0 };
 46→    }
 47→    if (w < BUTTON_SCALE_THRESHOLD) {
 48→      const s =
 49→        (w - SCALE_MIN_WIDTH) / (BUTTON_SCALE_THRESHOLD - SCALE_MIN_WIDTH);
 50→      return { transform: [{ scale: s }], opacity: s };
 51→    }
 52→    return { transform: [{ scale: 1 }], opacity: 1 };
 53→  });
 54→}
 55→
 56→const SWIPE_THRESHOLD = 60;
 57→const ACTION_BUTTON_WIDTH = 80;
 58→const BUTTON_GAP = 10;
 59→const BUTTON_SCALE_THRESHOLD = 56;
 60→const SCALE_MIN_WIDTH = 10;
 61→
 62→function SwipeableBookRow({
 63→  source,
 64→  recipeCount,
 65→  editingSourceId,
 66→  onDelete,
 67→  onStartEdit,
 68→  onConfirmEdit,
 69→  onCancelEdit,
 70→  editText,
 71→  onEditTextChange,
 72→}: {
 73→  source: Source;
 74→  recipeCount: number;
 75→  editingSourceId: SourceId | null;
 76→  onDelete: (sourceId: SourceId) => void;
 77→  onStartEdit: (sourceId: SourceId) => void;
 78→  onConfirmEdit: () => void;
 79→  onCancelEdit: () => void;
 80→  editText: string;
 81→  onEditTextChange: (text: string) => void;
 82→}): JSX.Element {
 83→  const { t } = useTranslation();
 84→  const theme = useTheme();
 85→  const colors = getColors(theme);
 86→  const isDark = theme === "dark";
 87→  const textColor = isDark ? "#FFFFFF" : "#000000";
 88→  const translateX = useSharedValue(0);
 89→  const startX = useSharedValue(0);
 90→  const isEditing = editingSourceId === source.id;
 91→  const isEditingShared = useSharedValue(isEditing);
 92→
 93→  isEditingShared.value = isEditing;
 94→
 95→  useEffect(() => {
 96→    translateX.value = withSpring(isEditing ? ACTION_BUTTON_WIDTH : 0);
 97→  }, [isEditing, translateX]);
 98→
 99→  const triggerDelete = useCallback(() => {
100→    translateX.value = withSpring(0);
101→    onDelete(source.id);
102→  }, [source.id, onDelete, translateX]);
103→
104→  const handleEditPress = useCallback(() => {
105→    if (isEditing) {
106→      onConfirmEdit();
107→    } else {
108→      onStartEdit(source.id);
109→    }
110→  }, [isEditing, onConfirmEdit, onStartEdit, source.id]);
111→
112→  const pan = Gesture.Pan()
113→    .activeOffsetX([-10, 10])
114→    .failOffsetY([-10, 10])
115→    .onStart(() => {
116→      startX.value = translateX.value;
117→    })
118→    .onUpdate((event) => {
119→      const raw = startX.value + event.translationX;
120→      const limit = ACTION_BUTTON_WIDTH * 1.5;
121→      translateX.value = Math.max(-limit, Math.min(limit, raw));
122→    })
123→    .onEnd(() => {
124→      if (isEditingShared.value) {
125→        if (translateX.value < SWIPE_THRESHOLD / 2) {
126→          translateX.value = withSpring(0);
127→          runOnJS(onCancelEdit)();
128→        } else {
129→          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
130→        }
131→      } else {
132→        if (translateX.value < -SWIPE_THRESHOLD) {
133→          translateX.value = withSpring(-ACTION_BUTTON_WIDTH);
134→        } else if (translateX.value > SWIPE_THRESHOLD) {
135→          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
136→        } else {
137→          translateX.value = withSpring(0);
138→        }
139→      }
140→    });
141→
142→  const tap = Gesture.Tap().onEnd(() => {
143→    if (Math.abs(translateX.value) > 10 && !isEditingShared.value) {
144→      translateX.value = withSpring(0);
145→    }
146→  });
147→
148→  const gesture = Gesture.Race(pan, tap);
149→
150→  const contentAnimStyle = useAnimatedStyle(() => ({
151→    transform: [{ translateX: translateX.value }],
152→  }));
153→
154→  const contentBgStyle = useAnimatedStyle(() => {
155→    const absX = Math.abs(translateX.value);
156→    const progress = Math.min(absX / SWIPE_THRESHOLD, 1);
157→    const bgColor = interpolateColor(
158→      progress,
159→      [0, 1],
160→      ["rgba(128,128,128,0)", "rgba(128,128,128,0.12)"]
161→    );
162→    return {
163→      backgroundColor: bgColor,
164→      borderRadius: interpolate(progress, [0, 1], [0, 18]),
165→    };
166→  });
167→
168→  const editButtonWidthStyle = useAnimatedStyle(() => ({
169→    width: translateX.value > BUTTON_GAP ? translateX.value - BUTTON_GAP : 0,
170→    opacity: translateX.value > BUTTON_GAP ? 1 : 0,
171→  }));
172→
173→  const deleteButtonWidthStyle = useAnimatedStyle(() => ({
174→    width: -translateX.value > BUTTON_GAP ? -translateX.value - BUTTON_GAP : 0,
175→    opacity: -translateX.value > BUTTON_GAP ? 1 : 0,
176→  }));
177→
178→  const editScaleStyle = useActionButtonScale(translateX, 1);
179→  const deleteScaleStyle = useActionButtonScale(translateX, -1);
180→
181→  return (
182→    <View style={styles.rowContainer}>
183→      {/* Edit button - left */}
184→      <Animated.View
185→        style={[
186→          styles.actionButtonContainer,
187→          styles.editButtonContainer,
188→          editButtonWidthStyle,
189→        ]}
190→      >
191→        <Pressable
192→          onPress={handleEditPress}
193→          style={styles.actionButtonPressable}
194→          accessibilityRole="button"
195→          accessibilityLabel={
196→            isEditing
197→              ? t("manageBooks.confirmEditAction")
198→              : t("manageBooks.editAction")
199→          }
200→        >
201→          <Animated.View style={[styles.actionButtonContent, editScaleStyle]}>
202→            <View style={[styles.actionButtonIcon, styles.editButtonColor]}>
203→              <MaterialIcons
204→                name={isEditing ? "check" : "edit"}
205→                size={20}
206→                color="#FFFFFF"
207→              />
208→            </View>
209→            <Text style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
210→              {isEditing
211→                ? t("manageBooks.confirmEditAction")
212→                : t("manageBooks.editAction")}
213→            </Text>
214→          </Animated.View>
215→        </Pressable>
216→      </Animated.View>
217→
218→      {/* Delete button - right */}
219→      <Animated.View
220→        style={[
221→          styles.actionButtonContainer,
222→          styles.deleteButtonContainer,
223→          deleteButtonWidthStyle,
224→        ]}
225→      >
226→        <Pressable
227→          onPress={triggerDelete}
228→          style={styles.actionButtonPressable}
229→          accessibilityRole="button"
230→          accessibilityLabel={t("manageBooks.deleteConfirmAction")}
231→        >
232→          <Animated.View style={[styles.actionButtonContent, deleteScaleStyle]}>
233→            <View style={[styles.actionButtonIcon, styles.deleteButtonColor]}>
234→              <MaterialIcons name="delete" size={20} color="#FFFFFF" />
235→            </View>
236→            <Text style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
237→              {t("manageBooks.deleteConfirmAction")}
238→            </Text>
239→          </Animated.View>
240→        </Pressable>
241→      </Animated.View>
242→
243→      {/* Content */}
244→      <GestureDetector gesture={gesture}>
245→        <Animated.View style={[styles.listItem, contentAnimStyle, contentBgStyle]}>
246→          {isEditing ? (
247→            <>
248→              <TextInput
249→                style={[styles.editInput, { color: textColor }]}
250→                value={editText}
251→                onChangeText={onEditTextChange}
252→                maxLength={100}
253→                autoFocus
254→                selectTextOnFocus
255→                returnKeyType="done"
256→                onSubmitEditing={onConfirmEdit}
257→              />
258→              <Text
259→                style={[styles.recipeCount, { color: colors.textSecondary }]}
260→              >
261→                {t("manageBooks.recipeCount", { count: recipeCount })}
262→              </Text>
263→            </>
264→          ) : (
265→            <>
266→              <Text style={[styles.bookTitle, { color: textColor }]}>
267→                {source.name}
268→              </Text>
269→              <Text
270→                style={[styles.recipeCount, { color: colors.textSecondary }]}
271→              >
272→                {t("manageBooks.recipeCount", { count: recipeCount })}
273→              </Text>
274→            </>
275→          )}
276→        </Animated.View>
277→      </GestureDetector>
278→
279→      <View
280→        style={[styles.separator, { backgroundColor: colors.separator }]}
281→      />
282→    </View>
283→  );
284→}
285→
286→export default function ManageBooksScreen(): JSX.Element {
287→  const { t } = useTranslation();
288→  const theme = useTheme();
289→  const colors = getColors(theme);
290→  const insets = useSafeAreaInsets();
291→  const isDark = theme === "dark";
292→  const textColor = isDark ? "#FFFFFF" : "#000000";
293→  const { sources, createSource, renameSource, deleteSource } = useSources();
294→  const { recipes, deleteRecipe } = useRecipes();
295→  const isDeletingRef = useRef(false);
296→
297→  const [isAddingBook, setIsAddingBook] = useState(false);
298→  const [newBookName, setNewBookName] = useState("");
299→  const [editingSourceId, setEditingSourceId] = useState<SourceId | null>(null);
300→  const [editText, setEditText] = useState("");
301→
302→  const recipeCounts = useMemo(() => {
303→    const counts = new Map<SourceId, number>();
304→    for (const recipe of recipes) {
305→      const src = recipe.metadata.source;
306→      if (src && !isUrl(src)) {
307→        counts.set(src, (counts.get(src) ?? 0) + 1);
308→      }
309→    }
310→    return counts;
311→  }, [recipes]);
312→
313→  const handleStartAdd = useCallback(() => {
314→    setIsAddingBook(true);
315→    setNewBookName("");
316→  }, []);
317→
318→  const handleCancelAdd = useCallback(() => {
319→    setIsAddingBook(false);
320→    setNewBookName("");
321→  }, []);
322→
323→  const handleConfirmAdd = useCallback(async () => {
324→    const trimmed = newBookName.trim();
325→    if (!trimmed) return;
326→    try {
327→      await createSource(trimmed);
328→      setIsAddingBook(false);
329→      setNewBookName("");
330→    } catch (error) {
331→      const message = error instanceof Error ? error.message : "";
332→      if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
333→        console.warn("Unexpected error adding book:", error);
334→      }
335→    }
336→  }, [newBookName, createSource]);
337→
338→  const handleDeleteBook = useCallback(
339→    (sourceId: SourceId) => {
340→      if (isDeletingRef.current) return;
341→      const source = sources.find((s) => s.id === sourceId);
342→      if (!source) return;
343→      const count = recipeCounts.get(sourceId) ?? 0;
344→
345→      Alert.alert(
346→        t("manageBooks.deleteConfirmTitle", { name: source.name }),
347→        t("manageBooks.deleteConfirmMessage", { count }),
348→        [
349→          { text: t("manageBooks.deleteConfirmCancel"), style: "cancel" },
350→          {
351→            text: t("manageBooks.deleteConfirmAction"),
352→            style: "destructive",
353→            onPress: async () => {
354→              isDeletingRef.current = true;
355→              try {
356→                if (Platform.OS !== "web") {
357→                  Haptics.notificationAsync(
358→                    Haptics.NotificationFeedbackType.Warning
359→                  );
360→                }
361→                const recipesToDelete = recipes.filter(
362→                  (r) => r.metadata.source === sourceId
363→                );
364→                for (const r of recipesToDelete) {
365→                  await deleteRecipe(r.id);
366→                }
367→                LayoutAnimation.configureNext(
368→                  LayoutAnimation.Presets.easeInEaseOut
369→                );
370→                await deleteSource(sourceId);
371→              } finally {
372→                isDeletingRef.current = false;
373→              }
374→            },
375→          },
376→        ]
377→      );
378→    },
379→    [sources, recipeCounts, recipes, deleteRecipe, deleteSource, t]
380→  );
381→
382→  const handleStartEdit = useCallback(
383→    (sourceId: SourceId) => {
384→      const source = sources.find((s) => s.id === sourceId);
385→      if (!source) return;
386→      setEditingSourceId(sourceId);
387→      setEditText(source.name);
388→    },
389→    [sources]
390→  );
391→
392→  const handleConfirmEdit = useCallback(async () => {
393→    if (!editingSourceId) return;
394→    const trimmed = editText.trim();
395→    if (trimmed) {
396→      try {
397→        await renameSource(editingSourceId, trimmed);
398→      } catch (error) {
399→        const message = error instanceof Error ? error.message : "";
400→        if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
401→          console.warn("Unexpected error renaming book:", error);
402→        }
403→      }
404→    }
405→    setEditingSourceId(null);
406→    setEditText("");
407→  }, [editingSourceId, editText, renameSource]);
408→
409→  const handleCancelEdit = useCallback(() => {
410→    setEditingSourceId(null);
411→    setEditText("");
412→  }, []);
413→
414→  return (
415→    <View style={[styles.container, { backgroundColor: colors.background }]}>
416→      <View style={[styles.header, { paddingTop: insets.top + 16 }]}>
417→        <Text style={[styles.headerTitle, { color: textColor }]}>
418→          {t("manageBooks.title")}
419→        </Text>
420→      </View>
421→
422→      {sources.length === 0 && !isAddingBook ? (
423→        <View style={styles.emptyState}>
424→          <Text style={[styles.emptyStateText, { color: colors.textSecondary }]}>
425→            {t("manageBooks.emptyState")}
426→          </Text>
427→        </View>
428→      ) : (
429→        <ScrollView
430→          style={styles.list}
431→          contentContainerStyle={styles.listContent}
432→        >
433→          {sources.map((source) => (
434→            <SwipeableBookRow
435→              key={source.id}
436→              source={source}
437→              recipeCount={recipeCounts.get(source.id) ?? 0}
438→              editingSourceId={editingSourceId}
439→              onDelete={handleDeleteBook}
440→              onStartEdit={handleStartEdit}
441→              onConfirmEdit={handleConfirmEdit}
442→              onCancelEdit={handleCancelEdit}
443→              editText={editText}
444→              onEditTextChange={setEditText}
445→            />
446→          ))}
447→        </ScrollView>
448→      )}
449→
450→      <KeyboardAvoidingView
451→        behavior={Platform.OS === "ios" ? "padding" : "height"}
452→        style={styles.keyboardAvoid}
453→      >
454→        <View style={styles.bottomBar}>
455→          {isAddingBook ? (
456→            <>
457→              <LiquidGlassButton
458→                onPress={handleCancelAdd}
459→                systemImage="xmark"
460→                accessibilityLabel={t("manageBooks.deleteConfirmCancel")}
461→              />
462→              <View style={styles.addInput}>
463→                <LiquidGlassInput
464→                  value={newBookName}
465→                  onChangeText={setNewBookName}
466→                  placeholder={t("sourceSelector.addNewPlaceholder")}
467→                  variant="form"
468→                  style={styles.addInputField}
469→                  autoFocus
470→                  maxLength={100}
471→                  returnKeyType="done"
472→                  onSubmitEditing={handleConfirmAdd}
473→                />
474→              </View>
475→              <LiquidGlassButton
476→                onPress={handleConfirmAdd}
477→                systemImage="checkmark"
478→                accessibilityLabel={t("accessibility.confirm")}
479→              />
480→            </>
481→          ) : (
482→            <>
483→              <LiquidGlassButton
484→                onPress={router.back}
485→                systemImage="chevron.left"
486→                accessibilityLabel={t("manageBooks.back")}
487→              />
488→              <View style={styles.spacer} />
489→              <LiquidGlassButton
490→                onPress={handleStartAdd}
491→                systemImage="plus"
492→                accessibilityLabel={t("manageBooks.addBook")}
493→              />
494→            </>
495→          )}
496→        </View>
497→      </KeyboardAvoidingView>
498→    </View>
499→  );
500→}
501→
