    opacity: translateX.value > BUTTON_GAP ? 1 : 0,
  }));

  const deleteButtonWidthStyle = useAnimatedStyle(() => ({
    width: -translateX.value > BUTTON_GAP ? -translateX.value - BUTTON_GAP : 0,
    opacity: -translateX.value > BUTTON_GAP ? 1 : 0,
