            style: "destructive",
            onPress: async () => {
              isDeletingRef.current = true;
              try {
                if (Platform.OS !== "web") {
                  Haptics.notificationAsync(
                    Haptics.NotificationFeedbackType.Warning
                  );
                }
                const recipesToDelete = recipes.filter(
