  1→import { useRecipes } from "@/features/recipes-list/context/RecipesContext";
  2→import { useSources } from "@/features/sources/context/SourcesContext";
  3→import { Alert } from "@/lib/alert";
  4→import type { SourceId } from "@/lib/types/primitives";
  5→import type { Source } from "@/lib/types/source";
  6→import { isUrl } from "@/lib/utils/recipeValidation";
  7→import { LiquidGlassButton, LiquidGlassInput } from "@/modules/liquid-glass";
  8→import { useTranslation } from "@/platform/i18n/useTranslation";
  9→import { getColors } from "@/platform/theme/glassStyles";
 10→import { useTheme } from "@/platform/theme/useTheme";
 11→import { MaterialIcons } from "@expo/vector-icons";
 12→import * as Haptics from "expo-haptics";
 13→import { router } from "expo-router";
 14→import { useCallback, useEffect, useMemo, useRef, useState, type JSX } from "react";
 15→import {
 16→  KeyboardAvoidingView,
 17→  LayoutAnimation,
 18→  Platform,
 19→  Pressable,
 20→  ScrollView,
 21→  StyleSheet,
 22→  Text,
 23→  TextInput,
 24→  View,
 25→} from "react-native";
 26→import { Gesture, GestureDetector } from "react-native-gesture-handler";
 27→import Animated, {
 28→  type SharedValue,
 29→  interpolate,
 30→  interpolateColor,
 31→  runOnJS,
 32→  useAnimatedStyle,
 33→  useSharedValue,
 34→  withSpring,
 35→} from "react-native-reanimated";
 36→import { useSafeAreaInsets } from "react-native-safe-area-context";
 37→
 38→function useActionButtonScale(
 39→  translateX: SharedValue<number>,
 40→  sign: 1 | -1
 41→): { transform: Array<{ scale: number }>; opacity: number } {
 42→  return useAnimatedStyle(() => {
 43→    const w = Math.max(sign * translateX.value - BUTTON_GAP, 0);
 44→    if (w <= SCALE_MIN_WIDTH) {
 45→      return { transform: [{ scale: 0 }], opacity: 0 };
 46→    }
 47→    if (w < BUTTON_SCALE_THRESHOLD) {
 48→      const s =
 49→        (w - SCALE_MIN_WIDTH) / (BUTTON_SCALE_THRESHOLD - SCALE_MIN_WIDTH);
 50→      return { transform: [{ scale: s }], opacity: s };
 51→    }
 52→    return { transform: [{ scale: 1 }], opacity: 1 };
 53→  });
 54→}
 55→
 56→const SWIPE_THRESHOLD = 60;
 57→const ACTION_BUTTON_WIDTH = 80;
 58→const BUTTON_GAP = 12;
 59→const BUTTON_CONTENT_WIDTH = ACTION_BUTTON_WIDTH - BUTTON_GAP;
 60→const BUTTON_SCALE_THRESHOLD = BUTTON_CONTENT_WIDTH;
 61→const SCALE_MIN_WIDTH = 10;
 62→
 63→function SwipeableBookRow({
 64→  source,
 65→  recipeCount,
 66→  editingSourceId,
 67→  onDelete,
 68→  onStartEdit,
 69→  onConfirmEdit,
 70→  onCancelEdit,
 71→  editText,
 72→  onEditTextChange,
 73→}: {
 74→  source: Source;
 75→  recipeCount: number;
 76→  editingSourceId: SourceId | null;
 77→  onDelete: (sourceId: SourceId) => void;
 78→  onStartEdit: (sourceId: SourceId) => void;
 79→  onConfirmEdit: () => void;
 80→  onCancelEdit: () => void;
 81→  editText: string;
 82→  onEditTextChange: (text: string) => void;
 83→}): JSX.Element {
 84→  const { t } = useTranslation();
 85→  const theme = useTheme();
 86→  const colors = getColors(theme);
 87→  const isDark = theme === "dark";
 88→  const textColor = isDark ? "#FFFFFF" : "#000000";
 89→  const translateX = useSharedValue(0);
 90→  const startX = useSharedValue(0);
 91→  const isEditing = editingSourceId === source.id;
 92→  const isEditingShared = useSharedValue(isEditing);
 93→
 94→  isEditingShared.value = isEditing;
 95→
 96→  useEffect(() => {
 97→    translateX.value = withSpring(isEditing ? ACTION_BUTTON_WIDTH : 0);
 98→  }, [isEditing, translateX]);
 99→
100→  const triggerDelete = useCallback(() => {
101→    translateX.value = withSpring(0);
102→    onDelete(source.id);
103→  }, [source.id, onDelete, translateX]);
104→
105→  const handleEditPress = useCallback(() => {
106→    if (isEditing) {
107→      onConfirmEdit();
108→    } else {
109→      onStartEdit(source.id);
110→    }
111→  }, [isEditing, onConfirmEdit, onStartEdit, source.id]);
112→
113→  const pan = Gesture.Pan()
114→    .activeOffsetX([-10, 10])
115→    .failOffsetY([-10, 10])
116→    .onStart(() => {
117→      startX.value = translateX.value;
118→    })
119→    .onUpdate((event) => {
120→      const raw = startX.value + event.translationX;
121→      const limit = ACTION_BUTTON_WIDTH * 1.5;
122→      translateX.value = Math.max(-limit, Math.min(limit, raw));
123→    })
124→    .onEnd(() => {
125→      if (isEditingShared.value) {
126→        if (translateX.value < SWIPE_THRESHOLD / 2) {
127→          translateX.value = withSpring(0);
128→          runOnJS(onCancelEdit)();
129→        } else {
130→          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
131→        }
132→      } else {
133→        if (translateX.value < -SWIPE_THRESHOLD) {
134→          translateX.value = withSpring(-ACTION_BUTTON_WIDTH);
135→        } else if (translateX.value > SWIPE_THRESHOLD) {
136→          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
137→        } else {
138→          translateX.value = withSpring(0);
139→        }
140→      }
141→    });
142→
143→  const tap = Gesture.Tap().onEnd(() => {
144→    if (Math.abs(translateX.value) > 10 && !isEditingShared.value) {
145→      translateX.value = withSpring(0);
146→    }
147→  });
148→
149→  const gesture = Gesture.Race(pan, tap);
150→
151→  const contentAnimStyle = useAnimatedStyle(() => ({
152→    transform: [{ translateX: translateX.value }],
153→  }));
154→
155→  const contentBgStyle = useAnimatedStyle(() => {
156→    const absX = Math.abs(translateX.value);
157→    const progress = Math.min(absX / SWIPE_THRESHOLD, 1);
158→    const bgColor = interpolateColor(
159→      progress,
160→      [0, 1],
161→      isDark
162→        ? ["rgba(44,43,45,0)", "#2c2b2d"]
163→        : ["rgba(128,128,128,0)", "rgba(128,128,128,0.12)"]
164→    );
165→    return {
166→      backgroundColor: bgColor,
167→      borderRadius: interpolate(progress, [0, 1], [0, 21]),
168→    };
169→  });
170→
171→  const editButtonWidthStyle = useAnimatedStyle(() => ({
172→    width: translateX.value > BUTTON_GAP ? translateX.value - BUTTON_GAP : 0,
173→    opacity: translateX.value > BUTTON_GAP ? 1 : 0,
174→  }));
175→
176→  const deleteButtonWidthStyle = useAnimatedStyle(() => ({
177→    width: -translateX.value > BUTTON_GAP ? -translateX.value - BUTTON_GAP : 0,
178→    opacity: -translateX.value > BUTTON_GAP ? 1 : 0,
179→  }));
180→
181→  const editScaleStyle = useActionButtonScale(translateX, 1);
182→  const deleteScaleStyle = useActionButtonScale(translateX, -1);
183→
184→  return (
185→    <View style={styles.rowContainer}>
186→      {/* Edit button - left */}
187→      <Animated.View
188→        style={[
189→          styles.actionButtonContainer,
190→          styles.editButtonContainer,
191→          editButtonWidthStyle,
192→        ]}
193→      >
194→        <Pressable
195→          onPress={handleEditPress}
196→          style={styles.actionButtonPressable}
197→          accessibilityRole="button"
198→          accessibilityLabel={
199→            isEditing
200→              ? t("manageBooks.confirmEditAction")
201→              : t("manageBooks.editAction")
202→          }
203→        >
204→          <Animated.View style={[styles.actionButtonContent, editScaleStyle]}>
205→            <View style={[styles.actionButtonIcon, styles.editButtonColor]}>
206→              <MaterialIcons
207→                name={isEditing ? "check" : "edit"}
208→                size={26}
209→                color="#FFFFFF"
210→              />
211→            </View>
212→            <Text numberOfLines={1} style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
213→              {isEditing
214→                ? t("manageBooks.confirmEditAction")
215→                : t("manageBooks.editAction")}
216→            </Text>
217→          </Animated.View>
218→        </Pressable>
219→      </Animated.View>
220→
221→      {/* Delete button - right */}
222→      <Animated.View
223→        style={[
224→          styles.actionButtonContainer,
225→          styles.deleteButtonContainer,
226→          deleteButtonWidthStyle,
227→        ]}
228→      >
229→        <Pressable
230→          onPress={triggerDelete}
231→          style={styles.actionButtonPressable}
232→          accessibilityRole="button"
233→          accessibilityLabel={t("manageBooks.deleteConfirmAction")}
234→        >
235→          <Animated.View style={[styles.actionButtonContent, deleteScaleStyle]}>
236→            <View style={[styles.actionButtonIcon, styles.deleteButtonColor]}>
237→              <MaterialIcons name="delete" size={26} color="#FFFFFF" />
238→            </View>
239→            <Text numberOfLines={1} style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
240→              {t("manageBooks.deleteConfirmAction")}
241→            </Text>
242→          </Animated.View>
243→        </Pressable>
244→      </Animated.View>
245→
246→      {/* Content */}
247→      <GestureDetector gesture={gesture}>
248→        <Animated.View style={[styles.listItem, contentAnimStyle, contentBgStyle]}>
249→          {isEditing ? (
250→            <>
251→              <TextInput
252→                style={[styles.editInput, { color: textColor }]}
253→                value={editText}
254→                onChangeText={onEditTextChange}
255→                maxLength={100}
256→                autoFocus
257→                selectTextOnFocus
258→                returnKeyType="done"
259→                onSubmitEditing={onConfirmEdit}
260→              />
261→              <Text
262→                style={[styles.recipeCount, { color: colors.textSecondary }]}
263→              >
264→                {t("manageBooks.recipeCount", { count: recipeCount })}
265→              </Text>
266→            </>
267→          ) : (
268→            <>
269→              <Text style={[styles.bookTitle, { color: textColor }]}>
270→                {source.name}
271→              </Text>
272→              <Text
273→                style={[styles.recipeCount, { color: colors.textSecondary }]}
274→              >
275→                {t("manageBooks.recipeCount", { count: recipeCount })}
276→              </Text>
277→            </>
278→          )}
279→        </Animated.View>
280→      </GestureDetector>
281→
282→    </View>
283→  );
284→}
285→
286→export default function ManageBooksScreen(): JSX.Element {
287→  const { t } = useTranslation();
288→  const theme = useTheme();
289→  const colors = getColors(theme);
290→  const insets = useSafeAreaInsets();
291→  const isDark = theme === "dark";
292→  const textColor = isDark ? "#FFFFFF" : "#000000";
293→  const { sources, createSource, renameSource, deleteSource } = useSources();
294→  const { recipes, deleteRecipe } = useRecipes();
295→  const isDeletingRef = useRef(false);
296→
297→  const [isAddingBook, setIsAddingBook] = useState(false);
298→  const [newBookName, setNewBookName] = useState("");
299→  const [editingSourceId, setEditingSourceId] = useState<SourceId | null>(null);
300→  const [editText, setEditText] = useState("");
301→
302→  const recipeCounts = useMemo(() => {
303→    const counts = new Map<SourceId, number>();
304→    for (const recipe of recipes) {
305→      const src = recipe.metadata.source;
306→      if (src && !isUrl(src)) {
307→        counts.set(src, (counts.get(src) ?? 0) + 1);
308→      }
309→    }
310→    return counts;
311→  }, [recipes]);
312→
313→  const handleStartAdd = useCallback(() => {
314→    setIsAddingBook(true);
315→    setNewBookName("");
316→  }, []);
317→
318→  const handleCancelAdd = useCallback(() => {
319→    setIsAddingBook(false);
320→    setNewBookName("");
321→  }, []);
322→
323→  const handleConfirmAdd = useCallback(async () => {
324→    const trimmed = newBookName.trim();
325→    if (!trimmed) return;
326→    try {
327→      await createSource(trimmed);
328→      setIsAddingBook(false);
329→      setNewBookName("");
330→    } catch (error) {
331→      const message = error instanceof Error ? error.message : "";
332→      if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
333→        console.warn("Unexpected error adding book:", error);
334→      }
335→    }
336→  }, [newBookName, createSource]);
337→
338→  const handleDeleteBook = useCallback(
339→    (sourceId: SourceId) => {
340→      if (isDeletingRef.current) return;
341→      const source = sources.find((s) => s.id === sourceId);
342→      if (!source) return;
343→      const count = recipeCounts.get(sourceId) ?? 0;
344→
345→      Alert.alert(
346→        t("manageBooks.deleteConfirmTitle", { name: source.name }),
347→        t("manageBooks.deleteConfirmMessage", { count }),
348→        [
349→          { text: t("manageBooks.deleteConfirmCancel"), style: "cancel" },
350→          {
351→            text: t("manageBooks.deleteConfirmAction"),
352→            style: "destructive",
353→            onPress: async () => {
