import { BackButton } from "@/lib/components/atoms/BackButton";
import { LiquidGlassButton } from "@/modules/liquid-glass";
import { useTranslation } from "@/platform/i18n/useTranslation";
import { getColors } from "@/platform/theme/glassStyles";
import { useTheme } from "@/platform/theme/useTheme";
import * as WebBrowser from "expo-web-browser";
import { router } from "expo-router";
import { type JSX, useCallback } from "react";
import { Linking, Pressable, ScrollView, StyleSheet, Text, View } from "react-native";
import { useSafeAreaInsets } from "react-native-safe-area-context";

export default function AboutScreen(): JSX.Element {
  const { t } = useTranslation();
  const theme = useTheme();
  const colors = getColors(theme);
  const insets = useSafeAreaInsets();
  const isDark = theme === "dark";
  const headingColor = isDark ? "#FFFFFF" : "#000000";

  const handleGitHub = useCallback(() => {
    WebBrowser.openBrowserAsync("https://github.com/erykpiast/fasola");
  }, []);

  const handleFeedback = useCallback(() => {
    Linking.openURL("mailto:eryk.napierala@gmail.com?subject=fasola%20feedback");
  }, []);

  return (
    <View style={[styles.container, { backgroundColor: colors.background }]}>
      <View style={[styles.header, { paddingTop: insets.top + 16 }]}>
        <Text style={[styles.headerTitle, { color: headingColor }]}>
          {t("about.title")}
        </Text>
      </View>

      <ScrollView
        style={styles.scroll}
        contentContainerStyle={styles.scrollContent}
      >
        {/* Intro */}
        <View style={styles.section}>
          <Text style={[styles.body, { color: colors.textSecondary }]}>
            {t("about.intro")}
          </Text>
          <Pressable onPress={handleGitHub}>
            <Text style={[styles.link, { color: colors.text }]}>
              {t("about.githubLink")}
            </Text>
          </Pressable>
        </View>

        {/* Author */}
        <View style={styles.section}>
          <Text style={[styles.name, { color: headingColor }]}>
            {t("about.authorName")}
          </Text>
          <Text style={[styles.body, { color: colors.textSecondary }]}>
            {t("about.authorEmail")}
          </Text>
          <Text style={[styles.body, { color: colors.textSecondary }]}>
            {t("about.authorCopyright")}
          </Text>
          <Text style={[styles.body, { color: colors.textSecondary, marginTop: 8 }]}>
            {t("about.authorBio")}
          </Text>
        </View>

        {/* Get help */}
        <View style={styles.section}>
          <Text style={[styles.body, { color: colors.textSecondary }]}>
            {t("about.helpText")}
          </Text>
          <Text style={styles.arrow}>{"\u2198"}</Text>
        </View>
      </ScrollView>

      <BackButton onPress={router.back} />

      <LiquidGlassButton
        onPress={handleFeedback}
