 6→import { usePopoverTransition } from "@/features/photos/hooks/usePopoverTransition";
 7→
 8→export function useGlobalOptions(): {
 9→  visible: boolean;
10→  options: Array<{ id: string; label: string; systemImage: string }>;
11→  handlePress: () => void;
12→  handleSelect: (id: string) => void;
13→  handleDismiss: () => void;
14→  buttonStyle: ReturnType<typeof usePopoverTransition>["buttonStyle"];
15→} {
16→  const { t } = useTranslation();
17→  const [visible, setVisible] = useState(false);
18→  const { buttonStyle } = usePopoverTransition(visible);
19→
20→  const options = useMemo(
21→    () => [
22→      {
23→        id: "manage-books",
24→        label: t("menu.manageBooks"),
25→        systemImage: "books.vertical",
26→      },
27→      {
28→        id: "about",
29→        label: t("menu.about"),
30→        systemImage: "info.circle",
31→      },
32→    ],
33→    [t],
34→  );
35→
36→  const handlePress = useCallback(() => {
37→    if (Platform.OS !== "web") {
38→      Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
39→    }
40→    setVisible(true);
41→  }, []);
42→
43→  const handleSelect = useCallback((id: string) => {
44→    setVisible(false);
45→    if (id === "manage-books") {
46→      router.push("/manage-books");
47→    } else if (id === "about") {
48→      router.push("/about");
49→    }
50→  }, []);
51→
52→  const handleDismiss = useCallback(() => {
53→    setVisible(false);
54→  }, []);
55→
56→  return useMemo(
57→    () => ({
58→      visible,
59→      options,
60→      handlePress,
61→      handleSelect,
62→      handleDismiss,
63→      buttonStyle,
64→    }),
65→    [visible, options, handlePress, handleSelect, handleDismiss, buttonStyle],
66→  );
67→}
