 10→public final class LiquidGlassPopoverView: ExpoView {
 11→  private let hostingController: UIHostingController<LiquidGlassPopoverContent>
 12→  
 13→  private var isVisible: Bool = false
 14→  private var options: [PopoverOption] = []
 15→  private var buttonSize: CGFloat = 48
 16→  private var anchor: String = "bottomTrailing"
 17→  private var buttonOffsetX: CGFloat = 28
 18→  private var buttonOffsetY: CGFloat = 28
 19→  let onOptionSelect = EventDispatcher()
 20→  let onDismiss = EventDispatcher()
 21→  
 22→  public required init(appContext: AppContext? = nil) {
 23→    let content = LiquidGlassPopoverContent(
 24→      isVisible: false,
 25→      options: [],
 26→      buttonSize: 48,
 27→      anchor: "bottomTrailing",
 28→      buttonOffsetX: 28,
 29→      buttonOffsetY: 28,
 30→      onOptionSelect: { _ in },
 31→      onDismiss: { }
 32→    )
 33→    
 34→    hostingController = UIHostingController(rootView: content)
 35→    hostingController.view.backgroundColor = .clear
 36→
 37→    // Disable safe area and layout margin handling to prevent content offset
 38→    if #available(iOS 16.4, *) {
 39→      hostingController.safeAreaRegions = []
 40→    }
 41→    hostingController.view.insetsLayoutMarginsFromSafeArea = false
 42→    hostingController.view.layoutMargins = .zero
 43→    hostingController.view.directionalLayoutMargins = .zero
 44→
 45→    super.init(appContext: appContext)
 46→    
 47→    addSubview(hostingController.view)
 48→    clipsToBounds = false
 49→    hostingController.view.clipsToBounds = false
 50→    hostingController.view.preservesSuperviewLayoutMargins = false
 51→    
 52→    updateContent()
 53→  }
 54→  
 55→  public override func layoutSubviews() {
 56→    super.layoutSubviews()
 57→    hostingController.view.frame = bounds
 58→  }
 59→  
 60→  public override func didMoveToWindow() {
 61→    super.didMoveToWindow()
 62→    if window != nil {
 63→      if let parentVC = findViewController() {
 64→        parentVC.addChild(hostingController)
 65→        hostingController.didMove(toParent: parentVC)
 66→      }
 67→    } else {
 68→      hostingController.willMove(toParent: nil)
 69→      hostingController.removeFromParent()
 70→    }
 71→  }
 72→  
 73→  private func findViewController() -> UIViewController? {
 74→    var responder: UIResponder? = self
 75→    while let nextResponder = responder?.next {
 76→      if let viewController = nextResponder as? UIViewController {
 77→        return viewController
 78→      }
 79→      responder = nextResponder
 80→    }
 81→    return nil
 82→  }
 83→  
 84→  func setVisible(_ visible: Bool) {
 85→    isVisible = visible
 86→    updateContent()
 87→  }
 88→  
 89→  func setButtonSize(_ size: CGFloat) {
 90→    buttonSize = size
 91→    updateContent()
 92→  }
 93→
 94→  func setAnchor(_ value: String) {
 95→    anchor = value
 96→    updateContent()
 97→  }
 98→
 99→  func setButtonOffset(_ offset: [String: CGFloat]) {
100→    buttonOffsetX = offset["x"] ?? 28
101→    buttonOffsetY = offset["y"] ?? 28
102→    updateContent()
103→  }
104→
105→  func setOptions(_ optionsData: [[String: Any]]) {
106→    options = optionsData.compactMap { dict in
107→      guard let id = dict["id"] as? String,
108→            let label = dict["label"] as? String,
109→            let systemImage = dict["systemImage"] as? String else {
110→        return nil
111→      }
112→      return PopoverOption(id: id, label: label, systemImage: systemImage)
113→    }
114→    updateContent()
115→  }
116→  
117→  private func updateContent() {
118→    let content = LiquidGlassPopoverContent(
119→      isVisible: isVisible,
120→      options: options,
121→      buttonSize: buttonSize,
122→      anchor: anchor,
123→      buttonOffsetX: buttonOffsetX,
124→      buttonOffsetY: buttonOffsetY,
125→      onOptionSelect: { [weak self] optionId in
126→        self?.onOptionSelect(["id": optionId])
127→      },
128→      onDismiss: { [weak self] in
129→        self?.onDismiss()
130→      }
131→    )
132→    hostingController.rootView = content
133→  }
134→}
