136→struct HighlightButtonStyle: ButtonStyle {
137→  func makeBody(configuration: Configuration) -> some View {
138→    configuration.label
139→      .opacity(configuration.isPressed ? 0.5 : 1.0)
140→      .animation(.easeInOut(duration: 0.1), value: configuration.isPressed)
141→  }
142→}
143→
144→struct LiquidGlassPopoverContent: View {
145→  var isVisible: Bool
146→  var options: [PopoverOption]
147→  var buttonSize: CGFloat
148→  var anchor: String
149→  var buttonOffsetX: CGFloat
150→  var buttonOffsetY: CGFloat
151→  var onOptionSelect: (String) -> Void
152→  var onDismiss: () -> Void
153→
154→  @State private var expanded = false
155→  @State private var shrinkScale: CGFloat = 1.0
156→  @State private var panelSize: CGSize = CGSize(width: 200, height: 100)
157→  @State private var animationGeneration: Int = 0
158→
159→  private var currentWidth: CGFloat {
160→    let base = expanded ? panelSize.width : buttonSize
161→    return base * shrinkScale
162→  }
163→
164→  private var currentHeight: CGFloat {
165→    let base = expanded ? panelSize.height : buttonSize
166→    return base * shrinkScale
167→  }
168→
169→  private var currentCornerRadius: CGFloat {
170→    expanded ? 20 : (buttonSize * shrinkScale) / 2
171→  }
172→
173→  var body: some View {
174→    GeometryReader { _ in
175→      ZStack {
176→        if isVisible || expanded {
177→          anchoredLayout
178→            .frame(maxWidth: .infinity, maxHeight: .infinity)
179→            .contentShape(Rectangle())
180→            .onTapGesture {
181→              onDismiss()
182→            }
183→        }
184→
185→        // Hidden measurement view to capture panel natural size
186→        optionsList
187→          .fixedSize()
188→          .hidden()
189→          .onGeometryChange(for: CGSize.self) { proxy in
190→            proxy.size
191→          } action: { newSize in
192→            panelSize = newSize
193→          }
194→      }
195→    }
196→    .ignoresSafeArea()
197→    .onChange(of: isVisible) { _, newValue in
198→      // Generation counter cancels stale Phase 2 callbacks on rapid open/dismiss.
199→      // The dismiss branch must reset shrinkScale to 1.0 to handle the case where
200→      // Phase 1 set it to 0.8 but Phase 2 was cancelled by the guard.
201→      animationGeneration += 1
202→      let currentGeneration = animationGeneration
203→      if newValue {
204→        // Phase 1: Shrink the button circle to 80%
205→        withAnimation(.spring(duration: 0.1)) {
206→          shrinkScale = 0.8
207→        }
208→        // Phase 2: Expand to panel size
209→        DispatchQueue.main.asyncAfter(deadline: .now() + 0.08) {
210→          guard animationGeneration == currentGeneration else { return }
211→          withAnimation(.spring(duration: 0.35, bounce: 0.3)) {
212→            expanded = true
213→            shrinkScale = 1.0
214→          }
215→        }
216→      } else {
217→        // Reverse: collapse to circle in one spring
218→        withAnimation(.spring(duration: 0.3, bounce: 0.1)) {
219→          expanded = false
220→          shrinkScale = 1.0
221→        }
222→      }
223→    }
224→  }
225→
226→  private var morphContent: some View {
227→    ZStack {
228→      // Expanded content (options list)
229→      optionsList
230→        .fixedSize()
231→        .opacity(expanded ? 1 : 0)
232→    }
233→    .frame(width: currentWidth, height: currentHeight)
234→  }
235→
236→  @ViewBuilder
237→  private var morphingContainer: some View {
238→    if #available(iOS 26.0, *) {
239→      morphContent
240→        .glassEffect(.regular, in: RoundedRectangle(cornerRadius: currentCornerRadius))
241→    } else {
242→      morphContent
243→        .background(.ultraThinMaterial)
244→        .clipShape(RoundedRectangle(cornerRadius: currentCornerRadius))
245→    }
246→  }
247→
248→  @ViewBuilder
249→  private var anchoredLayout: some View {
250→    if anchor == "topTrailing" {
251→      VStack {
252→        HStack {
253→          Spacer()
254→          morphingContainer
255→        }
256→        Spacer()
257→      }
258→      .padding(.trailing, buttonOffsetX)
259→      .padding(.top, buttonOffsetY)
260→    } else {
261→      VStack {
262→        Spacer()
263→        HStack {
264→          Spacer()
265→          morphingContainer
266→        }
267→      }
268→      .padding(.trailing, buttonOffsetX)
269→      .padding(.bottom, buttonOffsetY)
270→    }
271→  }
272→
273→  private var optionsList: some View {
274→    VStack(alignment: .leading, spacing: 0) {
275→      ForEach(Array(options.enumerated()), id: \.element.id) { index, option in
276→        Button(action: {
277→          let generator = UIImpactFeedbackGenerator(style: .light)
278→          generator.impactOccurred()
279→          onOptionSelect(option.id)
280→        }) {
281→          HStack(spacing: 12) {
282→            Image(systemName: option.systemImage)
283→              .font(.system(size: 20))
284→              .frame(width: 24)
285→            Text(option.label)
286→              .font(.body)
287→          }
288→          .padding(.horizontal, 16)
289→          .padding(.vertical, 14)
290→        }
291→        .buttonStyle(HighlightButtonStyle())
292→
293→        if index < options.count - 1 {
294→          Divider()
295→        }
296→      }
297→    }
298→  }
299→}
