 14→import { useSafeAreaInsets } from "react-native-safe-area-context";
 15→import { useDebugContext } from "../features/photo-adjustment/context/DebugContext";
 16→import { EmptyState } from "../features/photos/components/EmptyState";
 17→import {
 18→  usePhotoImport,
 19→  type ImportOption,
 20→} from "../features/photos/hooks/usePhotoImport";
 21→import { usePopoverTransition } from "../features/photos/hooks/usePopoverTransition";
 22→import { AddRecipeButton } from "../features/recipe-form/components/AddRecipeButton";
 23→import { RecipeGrid } from "../features/recipes-list/components/RecipeGrid";
 24→import { useRecipes } from "../features/recipes-list/context/RecipesContext";
 25→import { useRecipeFilter } from "../features/recipes-list/hooks/useRecipeFilter";
 26→import { SearchBar } from "../features/search/components/SearchBar";
 27→import { useSearchFocus } from "../features/search/hooks/useSearchFocus";
 28→import type { RecipeId } from "../lib/types/primitives";
 29→import { useTranslation } from "../platform/i18n/useTranslation";
 30→import { getColors } from "../platform/theme/glassStyles";
 31→import { useTheme } from "../platform/theme/useTheme";
 32→
 33→function ErrorFallback({ error }: { error?: Error }): JSX.Element {
 34→  return (
 35→    <View style={styles.errorContainer}>
 36→      <Text style={styles.errorTitle}>Error loading recipes</Text>
 37→      {error && <Text style={styles.errorMessage}>{error.message}</Text>}
 38→    </View>
 39→  );
 40→}
 41→
 42→function Content(): JSX.Element {
 43→  const theme = useTheme();
 44→  const colors = getColors(theme);
 45→  const { t } = useTranslation();
 46→  const insets = useSafeAreaInsets();
 47→  const { recipes } = useRecipes();
 48→  const { filteredRecipes, searchTerm, setSearchTerm } =
 49→    useRecipeFilter(recipes);
 50→  const { handleFocus, handleBlur, key } = useSearchFocus();
 51→  const { setDebugData } = useDebugContext();
 52→  const {
 53→    showPopover,
 54→    handleOptionSelect,
 55→    popoverVisible,
 56→    dismissPopover,
 57→    isImporting,
 58→  } = usePhotoImport();
 59→  const { searchBarStyle, buttonStyle } =
 60→    usePopoverTransition(popoverVisible, isImporting);
 61→
 62→  const [overflowVisible, setOverflowVisible] = useState(false);
 63→
 64→  useEffect(() => {
 65→    return () => {
 66→      setDebugData(null);
 67→    };
 68→  }, [setDebugData]);
 69→
 70→  const handleRecipeTap = useCallback((id: RecipeId): void => {
 71→    router.push(`/recipe/${id}`);
 72→  }, []);
 73→
 74→  const handleImportOptionSelect = useCallback(
 75→    (id: string) => {
 76→      handleOptionSelect(id as ImportOption);
 77→    },
 78→    [handleOptionSelect],
 79→  );
 80→
 81→  const importOptions = useMemo(
 82→    () => [
 83→      { id: "camera", label: t("addRecipe.camera"), systemImage: "camera" },
 84→      {
 85→        id: "library",
 86→        label: t("addRecipe.library"),
 87→        systemImage: "photo.on.rectangle",
 88→      },
 89→    ],
 90→    [t],
 91→  );
 92→
 93→  const overflowOptions = useMemo(
 94→    () => [
 95→      {
 96→        id: "manage-books",
 97→        label: t("menu.manageBooks"),
 98→        systemImage: "books.vertical",
 99→      },
100→    ],
101→    [t],
102→  );
103→
104→  const handleOverflowPress = useCallback(() => {
105→    if (Platform.OS !== "web") {
106→      Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
107→    }
108→    setOverflowVisible(true);
109→  }, []);
110→
111→  const handleOverflowSelect = useCallback((id: string) => {
112→    setOverflowVisible(false);
113→    if (id === "manage-books") {
114→      router.push("/manage-books");
115→    }
116→  }, []);
117→
118→  const handleOverflowDismiss = useCallback(() => {
119→    setOverflowVisible(false);
120→  }, []);
121→
122→  return (
123→    <View style={[styles.container, { backgroundColor: colors.background }]}>
124→      {recipes.length === 0 ? (
125→        <EmptyState />
126→      ) : (
127→        <RecipeGrid recipes={filteredRecipes} onRecipeTap={handleRecipeTap} />
128→      )}
129→
130→      {/* Overflow menu button - top right */}
131→      <View
132→        style={[styles.overflowButton, { top: insets.top + 8 }]}
133→        pointerEvents={popoverVisible || overflowVisible ? "none" : "auto"}
134→      >
135→        <LiquidGlassButton
136→          onPress={handleOverflowPress}
137→          systemImage="ellipsis"
138→          accessibilityLabel={t("menu.manageBooks")}
139→        />
140→      </View>
141→
142→      <KeyboardAvoidingView
143→        behavior={Platform.OS === "ios" ? "padding" : "height"}
144→        style={styles.keyboardAvoid}
145→      >
146→        <View style={styles.bottomBar}>
147→          <Animated.View style={[styles.searchBarWrapper, searchBarStyle]}>
148→            <SearchBar
149→              key={key}
150→              value={searchTerm}
151→              onChangeText={setSearchTerm}
152→              onFocus={handleFocus}
153→              onBlur={handleBlur}
154→            />
155→          </Animated.View>
156→          <Animated.View style={buttonStyle}>
157→            <AddRecipeButton onPress={showPopover} />
158→          </Animated.View>
159→        </View>
160→      </KeyboardAvoidingView>
161→
162→      {/* Import popover (bottom trailing) */}
163→      <View
164→        style={[
165→          StyleSheet.absoluteFill,
166→          !popoverVisible && isImporting && { opacity: 0 },
167→        ]}
168→        pointerEvents={popoverVisible ? "auto" : "none"}
169→      >
170→        <LiquidGlassPopover
171→          visible={popoverVisible}
172→          options={importOptions}
173→          buttonSize={56}
174→          onSelect={handleImportOptionSelect}
175→          onDismiss={dismissPopover}
176→        />
177→      </View>
178→
179→      {/* Overflow popover (top trailing) */}
180→      <View
181→        style={StyleSheet.absoluteFill}
182→        pointerEvents={overflowVisible ? "auto" : "none"}
183→      >
184→        <LiquidGlassPopover
185→          visible={overflowVisible}
186→          anchor="topTrailing"
187→          buttonOffset={{ x: 28, y: insets.top + 8 }}
188→          options={overflowOptions}
189→          buttonSize={44}
190→          onSelect={handleOverflowSelect}
191→          onDismiss={handleOverflowDismiss}
192→        />
193→      </View>
194→    </View>
195→  );
196→}
197→
198→export default function Index(): JSX.Element {
199→  return (
200→    <ErrorBoundary FallbackComponent={ErrorFallback}>
201→      <Suspense fallback={<View style={styles.suspenseFallback} />}>
202→        <Content />
203→      </Suspense>
204→    </ErrorBoundary>
205→  );
206→}
