220→    left: 0,
221→    right: 0,
222→  },
223→  bottomBar: {
224→    flexDirection: "row",
225→    gap: 12,
226→    // NOTE: The same effective space from the screen to button edges for the `Add note` button in the Apple Notes app
227→    paddingHorizontal: 28,
228→    paddingBottom: 28,
229→  },
230→  searchBarWrapper: {
231→    flex: 1,
232→  },
233→  suspenseFallback: {
234→    flex: 1,
235→  },
236→  errorContainer: {
237→    flex: 1,
238→    justifyContent: "center",
239→    alignItems: "center",
240→    padding: 20,
241→  },
242→  errorTitle: {
243→    color: "red",
