 71→    router.push(`/recipe/${id}`);
 72→  }, []);
 73→
 74→  const handleImportOptionSelect = useCallback(
 75→    (id: string) => {
 76→      handleOptionSelect(id as ImportOption);
 77→    },
 78→    [handleOptionSelect],
 79→  );
 80→
 81→  const importOptions = useMemo(
 82→    () => [
 83→      { id: "camera", label: t("addRecipe.camera"), systemImage: "camera" },
 84→      {
