133→        pointerEvents={popoverVisible || overflowVisible ? "none" : "auto"}
134→      >
135→        <LiquidGlassButton
136→          onPress={handleOverflowPress}
137→          systemImage="ellipsis"
138→          accessibilityLabel={t("menu.manageBooks")}
139→        />
140→      </View>
141→
142→      <KeyboardAvoidingView
143→        behavior={Platform.OS === "ios" ? "padding" : "height"}
144→        style={styles.keyboardAvoid}
145→      >
146→        <View style={styles.bottomBar}>
147→          <Animated.View style={[styles.searchBarWrapper, searchBarStyle]}>
148→            <SearchBar
149→              key={key}
150→              value={searchTerm}
151→              onChangeText={setSearchTerm}
152→              onFocus={handleFocus}
153→              onBlur={handleBlur}
154→            />
155→          </Animated.View>
156→          <Animated.View style={buttonStyle}>
157→            <AddRecipeButton onPress={showPopover} />
158→          </Animated.View>
159→        </View>
160→      </KeyboardAvoidingView>
161→
162→      {/* Import popover (bottom trailing) */}
163→      <View
164→        style={[
165→          StyleSheet.absoluteFill,
166→          !popoverVisible && isImporting && { opacity: 0 },
167→        ]}
168→        pointerEvents={popoverVisible ? "auto" : "none"}
169→      >
170→        <LiquidGlassPopover
171→          visible={popoverVisible}
172→          options={importOptions}
173→          buttonSize={56}
174→          onSelect={handleImportOptionSelect}
175→          onDismiss={dismissPopover}
176→        />
177→      </View>
178→
179→      {/* Overflow popover (top trailing) */}
180→      <View
181→        style={StyleSheet.absoluteFill}
182→        pointerEvents={overflowVisible ? "auto" : "none"}
183→      >
184→        <LiquidGlassPopover
185→          visible={overflowVisible}
186→          anchor="topTrailing"
187→          buttonOffset={{ x: 28, y: insets.top + 8 }}
188→          options={overflowOptions}
189→          buttonSize={44}
190→          onSelect={handleOverflowSelect}
191→          onDismiss={handleOverflowDismiss}
192→        />
193→      </View>
194→    </View>
195→  );
196→}
