return (
  <View style={[styles.container, { backgroundColor: colors.background }]}>
    {recipes.length === 0 ? (
      <EmptyState />
    ) : (
      <RecipeGrid recipes={filteredRecipes} onRecipeTap={handleRecipeTap} />
    )}

    {/* Overflow menu button - top right */}
    <View
      style={[styles.overflowButton, { top: insets.top + 8 }]}
      pointerEvents={popoverVisible || overflowVisible ? "none" : "auto"}
    >
      <LiquidGlassButton
        onPress={handleOverflowPress}
        systemImage="ellipsis"
        accessibilityLabel={t("menu.manageBooks")}
      />
    </View>

    <KeyboardAvoidingView
      behavior={Platform.OS === "ios" ? "padding" : "height"}
      style={styles.keyboardAvoid}
    >
      <View style={styles.bottomBar}>
        <Animated.View style={[styles.searchBarWrapper, searchBarStyle]}>
          <SearchBar
            key={key}
            value={searchTerm}
            onChangeText={setSearchTerm}
            onFocus={handleFocus}
            onBlur={handleBlur}
          />
        </Animated.View>
        <Animated.View style={buttonStyle}>
          <AddRecipeButton onPress={showPopover} />
        </Animated.View>
      </View>
    </KeyboardAvoidingView>

    {/* Import popover (bottom trailing) */}
    <View
      style={[
        StyleSheet.absoluteFill,
        !popoverVisible && isImporting && { opacity: 0 },
      ]}
      pointerEvents={popoverVisible ? "auto" : "none"}
    >
      <LiquidGlassPopover
        visible={popoverVisible}
        options={importOptions}
        buttonSize={56}
        onSelect={handleImportOptionSelect}
        onDismiss={dismissPopover}
      />
    </View>

    {/* Overflow popover (top trailing) */}
    <View
      style={StyleSheet.absoluteFill}
      pointerEvents={overflowVisible ? "auto" : "none"}
    >
      <LiquidGlassPopover
        visible={overflowVisible}
        anchor="topTrailing"
        buttonOffset={{ x: 28, y: insets.top + 8 }}
        options={overflowOptions}
        buttonSize={44}
        onSelect={handleOverflowSelect}
        onDismiss={handleOverflowDismiss}
      />
    </View>
  </View>
