  1import { useRecipes } from "@/features/recipes-list/context/RecipesContext";
  2import { useSources } from "@/features/sources/context/SourcesContext";
  3import { Alert } from "@/lib/alert";
  4import type { SourceId } from "@/lib/types/primitives";
  5import type { Source } from "@/lib/types/source";
  6import { isUrl } from "@/lib/utils/recipeValidation";
  7import { LiquidGlassButton, LiquidGlassInput } from "@/modules/liquid-glass";
  8import { useTranslation } from "@/platform/i18n/useTranslation";
  9import { getColors } from "@/platform/theme/glassStyles";
 10import { useTheme } from "@/platform/theme/useTheme";
 11import { MaterialIcons } from "@expo/vector-icons";
 12import * as Haptics from "expo-haptics";
 13import { router } from "expo-router";
 14import { useCallback, useMemo, useRef, useState, type JSX } from "react";
 15import {
 16  KeyboardAvoidingView,
 17  LayoutAnimation,
 18  Platform,
 19  ScrollView,
 20  StyleSheet,
 21  Text,
 22  TextInput,
 23  View,
 24} from "react-native";
 25import { Gesture, GestureDetector } from "react-native-gesture-handler";
 26import Animated, {
 27  runOnJS,
 28  useAnimatedStyle,
 29  useSharedValue,
 30  withSpring,
 31} from "react-native-reanimated";
 32import { useSafeAreaInsets } from "react-native-safe-area-context";
 33
 34const SWIPE_THRESHOLD = 80;
 35
 36function SwipeableBookRow({
 37  source,
 38  recipeCount,
 39  editingSourceId,
 40  onDelete,
 41  onStartEdit,
 42  onConfirmEdit,
 43  onCancelEdit,
 44  editText,
 45  onEditTextChange,
 46}: {
 47  source: Source;
 48  recipeCount: number;
 49  editingSourceId: SourceId | null;
 50  onDelete: (sourceId: SourceId) => void;
 51  onStartEdit: (sourceId: SourceId) => void;
 52  onConfirmEdit: () => void;
 53  onCancelEdit: () => void;
 54  editText: string;
 55  onEditTextChange: (text: string) => void;
 56}): JSX.Element {
 57  const { t } = useTranslation();
 58  const theme = useTheme();
 59  const colors = getColors(theme);
 60  const isDark = theme === "dark";
 61  const textColor = isDark ? "#FFFFFF" : "#000000";
 62  const translateX = useSharedValue(0);
 63  const isEditing = editingSourceId === source.id;
 64
 65  const triggerDelete = useCallback(() => {
 66    onDelete(source.id);
 67  }, [source.id, onDelete]);
 68
 69  const triggerEdit = useCallback(() => {
 70    onStartEdit(source.id);
 71  }, [source.id, onStartEdit]);
 72
 73  const pan = Gesture.Pan()
 74    .activeOffsetX([-10, 10])
 75    .failOffsetY([-10, 10])
 76    .onUpdate((event) => {
 77      translateX.value = event.translationX;
 78    })
 79    .onEnd((event) => {
 80      translateX.value = withSpring(0);
 81      if (event.translationX < -SWIPE_THRESHOLD) {
 82        runOnJS(triggerDelete)();
 83      } else if (event.translationX > SWIPE_THRESHOLD) {
 84        runOnJS(triggerEdit)();
 85      }
 86    });
 87
 88  const cancelPan = Gesture.Pan()
 89    .activeOffsetX([-10, 10])
 90    .failOffsetY([-10, 10])
 91    .onEnd((event) => {
 92      if (event.translationX > SWIPE_THRESHOLD) {
 93        runOnJS(onCancelEdit)();
 94      }
 95    });
 96
 97  const contentStyle = useAnimatedStyle(() => ({
 98    transform: [{ translateX: translateX.value }],
 99  }));
100
101  const deleteBackgroundStyle = useAnimatedStyle(() => ({
102    opacity: translateX.value < 0 ? Math.min(1, -translateX.value / SWIPE_THRESHOLD) : 0,
103  }));
104
105  const editBackgroundStyle = useAnimatedStyle(() => ({
106    opacity: translateX.value > 0 ? Math.min(1, translateX.value / SWIPE_THRESHOLD) : 0,
107  }));
108
109  return (
110    <GestureDetector gesture={isEditing ? cancelPan : pan}>
111      <Animated.View>
112        {/* Delete background (swipe left) */}
113        <Animated.View
114          style={[
115            styles.actionBackground,
116            styles.deleteBackground,
117            deleteBackgroundStyle,
118          ]}
119        >
120          <MaterialIcons name="delete" size={24} color="#FFFFFF" />
121        </Animated.View>
122
123        {/* Edit background (swipe right) */}
124        <Animated.View
125          style={[
126            styles.actionBackground,
127            styles.editBackground,
128            editBackgroundStyle,
129          ]}
130        >
131          <MaterialIcons
132            name={isEditing ? "check" : "edit"}
133            size={24}
134            color="#FFFFFF"
135          />
136        </Animated.View>
137
138        {/* Content */}
139        <Animated.View style={[styles.listItem, contentStyle]}>
140          {isEditing ? (
141            <>
142              <TextInput
143                style={[styles.editInput, { color: textColor }]}
144                value={editText}
145                onChangeText={onEditTextChange}
146                maxLength={100}
147                autoFocus
148                selectTextOnFocus
149                returnKeyType="done"
150                onSubmitEditing={onConfirmEdit}
151              />
152              <Text
153                style={[styles.recipeCount, { color: colors.textSecondary }]}
154              >
155                {t("manageBooks.recipeCount", { count: recipeCount })}
156              </Text>
157            </>
158          ) : (
159            <>
160              <Text style={[styles.bookTitle, { color: textColor }]}>
161                {source.name}
162              </Text>
163              <Text
164                style={[styles.recipeCount, { color: colors.textSecondary }]}
165              >
166                {t("manageBooks.recipeCount", { count: recipeCount })}
167              </Text>
168            </>
169          )}
170        </Animated.View>
171
172        <View
173          style={[
174            styles.separator,
175            { backgroundColor: colors.separator },
176          ]}
177        />
178      </Animated.View>
179    </GestureDetector>
180  );
181}
