183export default function ManageBooksScreen(): JSX.Element {
184  const { t } = useTranslation();
185  const theme = useTheme();
186  const colors = getColors(theme);
187  const insets = useSafeAreaInsets();
188  const isDark = theme === "dark";
189  const textColor = isDark ? "#FFFFFF" : "#000000";
190  const { sources, createSource, renameSource, deleteSource } = useSources();
191  const { recipes, deleteRecipe } = useRecipes();
192  const isDeletingRef = useRef(false);
193
194  const [isAddingBook, setIsAddingBook] = useState(false);
195  const [newBookName, setNewBookName] = useState("");
196  const [editingSourceId, setEditingSourceId] = useState<SourceId | null>(null);
197  const [editText, setEditText] = useState("");
198
199  const recipeCounts = useMemo(() => {
200    const counts = new Map<SourceId, number>();
201    for (const recipe of recipes) {
202      const src = recipe.metadata.source;
203      if (src && !isUrl(src)) {
204        counts.set(src, (counts.get(src) ?? 0) + 1);
205      }
206    }
207    return counts;
208  }, [recipes]);
209
210  const handleStartAdd = useCallback(() => {
211    setIsAddingBook(true);
212    setNewBookName("");
213  }, []);
214
215  const handleCancelAdd = useCallback(() => {
216    setIsAddingBook(false);
217    setNewBookName("");
218  }, []);
219
220  const handleConfirmAdd = useCallback(async () => {
221    const trimmed = newBookName.trim();
222    if (!trimmed) return;
223    try {
224      await createSource(trimmed);
225      setIsAddingBook(false);
226      setNewBookName("");
227    } catch (error) {
228      const message = error instanceof Error ? error.message : "";
229      if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
230        console.warn("Unexpected error adding book:", error);
231      }
232    }
233  }, [newBookName, createSource]);
234
235  const handleDeleteBook = useCallback(
236    (sourceId: SourceId) => {
237      if (isDeletingRef.current) return;
238      const source = sources.find((s) => s.id === sourceId);
239      if (!source) return;
240      const count = recipeCounts.get(sourceId) ?? 0;
241
242      Alert.alert(
243        t("manageBooks.deleteConfirmTitle", { name: source.name }),
244        t("manageBooks.deleteConfirmMessage", { count }),
245        [
246          { text: t("manageBooks.deleteConfirmCancel"), style: "cancel" },
247          {
248            text: t("manageBooks.deleteConfirmAction"),
249            style: "destructive",
250            onPress: async () => {
251              isDeletingRef.current = true;
252              try {
253                if (Platform.OS !== "web") {
254                  Haptics.notificationAsync(
255                    Haptics.NotificationFeedbackType.Warning
256                  );
257                }
258                const recipesToDelete = recipes.filter(
259                  (r) => r.metadata.source === sourceId
260                );
261                for (const r of recipesToDelete) {
262                  await deleteRecipe(r.id);
263                }
264                LayoutAnimation.configureNext(
265                  LayoutAnimation.Presets.easeInEaseOut
266                );
267                await deleteSource(sourceId);
268              } finally {
269                isDeletingRef.current = false;
270              }
271            },
272          },
273        ]
274      );
275    },
276    [sources, recipeCounts, recipes, deleteRecipe, deleteSource, t]
277  );
278
279  const handleStartEdit = useCallback(
280    (sourceId: SourceId) => {
281      const source = sources.find((s) => s.id === sourceId);
282      if (!source) return;
283      setEditingSourceId(sourceId);
284      setEditText(source.name);
285    },
286    [sources]
287  );
288
289  const handleConfirmEdit = useCallback(async () => {
290    if (!editingSourceId) return;
291    const trimmed = editText.trim();
292    if (trimmed) {
293      try {
294        await renameSource(editingSourceId, trimmed);
295      } catch (error) {
296        const message = error instanceof Error ? error.message : "";
297        if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
298          console.warn("Unexpected error renaming book:", error);
299        }
300      }
301    }
302    setEditingSourceId(null);
303    setEditText("");
304  }, [editingSourceId, editText, renameSource]);
305
306  const handleCancelEdit = useCallback(() => {
307    setEditingSourceId(null);
308    setEditText("");
309  }, []);
310
311  return (
312    <View style={[styles.container, { backgroundColor: colors.background }]}>
313      <View style={[styles.header, { paddingTop: insets.top + 16 }]}>
314        <Text style={[styles.headerTitle, { color: textColor }]}>
315          {t("manageBooks.title")}
316        </Text>
317      </View>
318
319      {sources.length === 0 && !isAddingBook ? (
320        <View style={styles.emptyState}>
321          <Text style={[styles.emptyStateText, { color: colors.textSecondary }]}>
322            {t("manageBooks.emptyState")}
323          </Text>
324        </View>
325      ) : (
326        <ScrollView
327          style={styles.list}
328          contentContainerStyle={styles.listContent}
329        >
330          {sources.map((source) => (
331            <SwipeableBookRow
332              key={source.id}
333              source={source}
334              recipeCount={recipeCounts.get(source.id) ?? 0}
335              editingSourceId={editingSourceId}
336              onDelete={handleDeleteBook}
337              onStartEdit={handleStartEdit}
338              onConfirmEdit={handleConfirmEdit}
339              onCancelEdit={handleCancelEdit}
340              editText={editText}
341              onEditTextChange={setEditText}
342            />
343          ))}
344        </ScrollView>
345      )}
346
347      <KeyboardAvoidingView
348        behavior={Platform.OS === "ios" ? "padding" : "height"}
349        style={styles.keyboardAvoid}
350      >
351        <View style={styles.bottomBar}>
352          {isAddingBook ? (
353            <>
354              <LiquidGlassButton
355                onPress={handleCancelAdd}
356                systemImage="xmark"
357                accessibilityLabel={t("manageBooks.deleteConfirmCancel")}
358              />
359              <View style={styles.addInput}>
360                <LiquidGlassInput
361                  value={newBookName}
362                  onChangeText={setNewBookName}
363                  placeholder={t("sourceSelector.addNewPlaceholder")}
364                  variant="form"
365                  style={styles.addInputField}
366                  autoFocus
367                  maxLength={100}
368                  returnKeyType="done"
369                  onSubmitEditing={handleConfirmAdd}
370                />
371              </View>
372              <LiquidGlassButton
373                onPress={handleConfirmAdd}
374                systemImage="checkmark"
375                accessibilityLabel={t("accessibility.confirm")}
376              />
377            </>
378          ) : (
379            <>
380              <LiquidGlassButton
381                onPress={router.back}
382                systemImage="chevron.left"
383                accessibilityLabel={t("manageBooks.back")}
384              />
385              <View style={styles.spacer} />
386              <LiquidGlassButton
387                onPress={handleStartAdd}
388                systemImage="plus"
389                accessibilityLabel={t("manageBooks.addBook")}
390              />
391            </>
392          )}
393        </View>
394      </KeyboardAvoidingView>
395    </View>
396  );
397}
