  1import { sourceRepository } from "@/lib/repositories/sources";
  2import type { SourceId } from "@/lib/types/primitives";
  3import type { Source } from "@/lib/types/source";
  4import {
  5  createContext,
  6  use,
  7  useCallback,
  8  useContext,
  9  useMemo,
 10  useState,
 11  type JSX,
 12  type ReactNode,
 13} from "react";
 14
 15type SourcesContextValue = {
 16  sources: Array<Source>;
 17  getSourceName: (id: SourceId) => string | undefined;
 18  createSource: (name: string) => Promise<Source>;
 19  renameSource: (id: SourceId, newName: string) => Promise<void>;
 20  deleteSource: (id: SourceId) => Promise<void>;
 21  touchSource: (id: SourceId) => Promise<void>;
 22  getLastUsed: () => Source | null;
 23  refresh: () => Promise<void>;
 24};
 25
 26const SourcesContext = createContext<SourcesContextValue | null>(null);
 27
 28let sourcesPromise: Promise<Array<Source>> | null = null;
 29
 30function getSourcesPromise(): Promise<Array<Source>> {
 31  if (!sourcesPromise) {
 32    sourcesPromise = sourceRepository.getAll().catch((error) => {
 33      sourcesPromise = null;
 34      throw error;
 35    });
 36  }
 37  return sourcesPromise;
 38}
 39
 40export function SourcesProvider({
 41  children,
 42}: {
 43  children: ReactNode;
 44}): JSX.Element {
 45  const initialSources = use(getSourcesPromise());
 46  const [sources, setSources] = useState<Array<Source>>(initialSources);
 47
 48  const getSourceName = useCallback(
 49    (id: SourceId): string | undefined => {
 50      return sources.find((s) => s.id === id)?.name;
 51    },
 52    [sources]
 53  );
 54
 55  const createSource = useCallback(async (name: string): Promise<Source> => {
 56    const newSource = await sourceRepository.create(name);
 57    setSources((prev) => [newSource, ...prev]);
 58    return newSource;
 59  }, []);
 60
 61  const renameSource = useCallback(
 62    async (id: SourceId, newName: string): Promise<void> => {
 63      await sourceRepository.rename(id, newName);
 64      setSources((prev) =>
 65        prev.map((s) => (s.id === id ? { ...s, name: newName.trim() } : s))
 66      );
 67    },
 68    []
 69  );
 70
 71  const deleteSource = useCallback(
 72    async (id: SourceId): Promise<void> => {
 73      await sourceRepository.delete(id);
 74      setSources((prev) => prev.filter((s) => s.id !== id));
 75    },
 76    []
 77  );
 78
 79  const touchSource = useCallback(
 80    async (id: SourceId): Promise<void> => {
 81      await sourceRepository.touch(id);
 82      setSources((prev) =>
 83        prev.map((s) =>
 84          s.id === id ? { ...s, lastUsedAt: Date.now() } : s
 85        )
 86      );
 87    },
 88    []
 89  );
 90
 91  const getLastUsed = useCallback((): Source | null => {
 92    if (sources.length === 0) return null;
 93    const sorted = [...sources].sort((a, b) => b.lastUsedAt - a.lastUsedAt);
 94    const mostRecent = sorted[0];
 95    const isWithin24Hours =
 96      Date.now() - mostRecent.lastUsedAt < 24 * 60 * 60 * 1000;
 97    return isWithin24Hours ? mostRecent : null;
 98  }, [sources]);
 99
100  const refresh = useCallback(async (): Promise<void> => {
101    const freshSources = await sourceRepository.getAll();
102    setSources(freshSources);
103  }, []);
104
105  const value = useMemo(
106    (): SourcesContextValue => ({
107      sources,
108      getSourceName,
109      createSource,
110      renameSource,
111      deleteSource,
112      touchSource,
113      getLastUsed,
114      refresh,
115    }),
116    [
117      sources,
118      getSourceName,
119      createSource,
120      renameSource,
121      deleteSource,
122      touchSource,
123      getLastUsed,
124      refresh,
125    ]
126  );
127
128  return (
129    <SourcesContext.Provider value={value}>{children}</SourcesContext.Provider>
130  );
131}
132
133export function useSources(): SourcesContextValue {
134  const ctx = useContext(SourcesContext);
135  if (!ctx) throw new Error("useSources must be used within SourcesProvider");
136  return ctx;
137}
