  1import { useSources } from "@/features/sources/context/SourcesContext";
  2import type { SourceId } from "@/lib/types/primitives";
  3import { LiquidGlassInput, LiquidGlassSelect } from "@/modules/liquid-glass";
  4import { useTranslation } from "@/platform/i18n/useTranslation";
  5import { getGlassInputColors } from "@/platform/theme/glassStyles";
  6import { useTheme, type Theme } from "@/platform/theme/useTheme";
  7import { Picker } from "@react-native-picker/picker";
  8import {
  9  forwardRef,
 10  useCallback,
 11  useEffect,
 12  useImperativeHandle,
 13  useState,
 14  type JSX,
 15} from "react";
 16import {
 17  Modal,
 18  Platform,
 19  Pressable,
 20  StyleSheet,
 21  Text,
 22  View,
 23} from "react-native";
 24
 25const ADD_NEW_VALUE = "__ADD_NEW__";
 26
 27export interface SourceSelectorRef {
 28  confirmNewSource: () => Promise<SourceId | undefined>;
 29  cancelEdit: () => void;
 30}
 31
 32export const SourceSelector = forwardRef<
 33  SourceSelectorRef,
 34  {
 35    value: SourceId;
 36    onValueChange: (sourceId: SourceId, isAutomatic?: boolean) => void;
 37    onInteraction?: () => void;
 38    onEditingChange?: (editing: boolean) => void;
 39    onHasNoSourcesChange?: (hasNoSources: boolean) => void;
 40  }
 41>(function SourceSelector(
 42  { value, onValueChange, onInteraction, onEditingChange, onHasNoSourcesChange },
 43  ref
 44): JSX.Element {
 45  const { t } = useTranslation();
 46  const theme = useTheme();
 47  const { sources, createSource, getLastUsed } = useSources();
 48
 49  const [isEditingNewSource, setIsEditingNewSource] = useState(false);
 50  const [pickerModalVisible, setPickerModalVisible] = useState(false);
 51  const [newSourceText, setNewSourceText] = useState("");
 52  const [tempValue, setTempValue] = useState(value);
 53
 54  const hasNoSources = sources.length === 0;
 55
 56  useEffect(() => {
 57    onHasNoSourcesChange?.(hasNoSources);
 58  }, [hasNoSources, onHasNoSourcesChange]);
 59
 60  useEffect(() => {
 61    if (!value) {
 62      const lastUsed = getLastUsed();
 63      if (lastUsed) {
 64        onValueChange(lastUsed.id, true);
 65      }
 66    }
 67  }, [getLastUsed, value, onValueChange]);
 68
 69  // Sync tempValue when value changes externally
 70  useEffect(() => {
 71    setTempValue(value);
 72  }, [value]);
 73
 74  const selectedSourceName = sources.find((s) => s.id === value)?.name ?? "";
 75
 76  const openPickerModal = useCallback(() => {
 77    setTempValue(value || (sources.length > 0 ? sources[0].id : ""));
 78    setPickerModalVisible(true);
 79    onInteraction?.();
 80  }, [value, sources, onInteraction]);
 81
 82  const handlePickerDone = useCallback(() => {
 83    setPickerModalVisible(false);
 84    if (tempValue === ADD_NEW_VALUE) {
 85      setIsEditingNewSource(true);
 86    } else if (tempValue) {
 87      onValueChange(tempValue, false);
 88    }
 89  }, [tempValue, onValueChange]);
 90
 91  const handlePickerCancel = useCallback(() => {
 92    setPickerModalVisible(false);
 93    setTempValue(value);
 94  }, [value]);
 95
 96  const handleConfirmNewSource = useCallback(async (): Promise<
 97    SourceId | undefined
 98  > => {
 99    const trimmedSource = newSourceText.trim();
100    if (!trimmedSource) {
101      return undefined;
102    }
103
104    const newSource = await createSource(trimmedSource);
105    onValueChange(newSource.id, false);
106    setIsEditingNewSource(false);
107    setNewSourceText("");
108    return newSource.id;
109  }, [newSourceText, createSource, onValueChange]);
110
111  const handleCancelEdit = useCallback(() => {
112    setIsEditingNewSource(false);
113    setNewSourceText("");
114  }, []);
115
116  useEffect(() => {
117    onEditingChange?.(isEditingNewSource);
118  }, [isEditingNewSource, onEditingChange]);
119
120  useImperativeHandle(
121    ref,
122    () => ({
123      confirmNewSource: handleConfirmNewSource,
124      cancelEdit: handleCancelEdit,
125    }),
126    [handleConfirmNewSource, handleCancelEdit]
127  );
128
129  const handleWebPickerChange = useCallback(
130    (itemValue: string) => {
131      if (itemValue === ADD_NEW_VALUE) {
132        setIsEditingNewSource(true);
133        onInteraction?.();
134      } else if (itemValue) {
135        onValueChange(itemValue, false);
136        onInteraction?.();
137      }
138    },
139    [onValueChange, onInteraction]
140  );
141
142  const isWeb = Platform.OS === "web";
143  const themeColors = getThemeColors(theme);
144
145  return (
146    <>
147      <View style={styles.container}>
148        {isWeb ? (
149          <Picker
150            selectedValue={value || ""}
151            onValueChange={handleWebPickerChange}
152            style={[styles.triggerButton, themeColors.triggerButton]}
153          >
154            {!value && (
155              <Picker.Item
156                label={t("sourceSelector.placeholder")}
157                value=""
158                enabled={false}
159              />
160            )}
161            {sources.map((source) => (
162              <Picker.Item
163                key={source.id}
164                label={source.name}
165                value={source.id}
