 1import * as Crypto from "expo-crypto";
 2import { storage } from "../storage";
 3import type { SourceId, StorageKey } from "../types/primitives";
 4import type { Source } from "../types/source";
 5
 6const SOURCES_KEY: StorageKey = "@sources";
 7const MAX_SOURCE_NAME_LENGTH = 100;
 8
 9let lockChain = Promise.resolve();
10
11function withLock<T>(fn: () => Promise<T>): Promise<T> {
12  const next = lockChain.then(fn, fn);
13  lockChain = next.then(() => {}, () => {});
14  return next;
15}
16
17class SourceRepository {
18  async getAll(): Promise<Array<Source>> {
19    const data = await storage.getItem(SOURCES_KEY);
20    if (!data) return [];
21    try {
22      const sources: Array<Source> = JSON.parse(data);
23      return sources.sort((a, b) => b.lastUsedAt - a.lastUsedAt);
24    } catch {
25      return [];
26    }
27  }
28
29  async getById(id: SourceId): Promise<Source | null> {
30    const sources = await this.getAll();
31    return sources.find((s) => s.id === id) ?? null;
32  }
33
34  async create(name: string): Promise<Source> {
35    return withLock(async () => {
36      const trimmed = name.trim();
37      if (!trimmed) throw new Error("Source name cannot be empty");
38      if (trimmed.length > MAX_SOURCE_NAME_LENGTH) {
39        throw new Error("Source name is too long");
40      }
41      const sources = await this.getAll();
42      if (sources.some((s) => s.name.toLowerCase() === trimmed.toLowerCase())) {
43        throw new Error("A book with this name already exists");
44      }
45      const newSource: Source = {
46        id: Crypto.randomUUID(),
47        name: trimmed,
48        lastUsedAt: Date.now(),
49      };
50      sources.push(newSource);
51      await storage.setItem(SOURCES_KEY, JSON.stringify(sources));
52      return newSource;
53    });
54  }
55
56  async rename(id: SourceId, newName: string): Promise<void> {
57    return withLock(async () => {
58      const trimmed = newName.trim();
59      if (!trimmed) throw new Error("Source name cannot be empty");
60      if (trimmed.length > MAX_SOURCE_NAME_LENGTH) {
61        throw new Error("Source name is too long");
62      }
63      const sources = await this.getAll();
64      if (
65        sources.some(
66          (s) => s.id !== id && s.name.toLowerCase() === trimmed.toLowerCase()
67        )
68      ) {
69        throw new Error("A book with this name already exists");
70      }
71      const source = sources.find((s) => s.id === id);
72      if (!source) return;
73      source.name = trimmed;
74      await storage.setItem(SOURCES_KEY, JSON.stringify(sources));
75    });
76  }
77
78  async delete(id: SourceId): Promise<void> {
79    return withLock(async () => {
80      const sources = await this.getAll();
81      const filtered = sources.filter((s) => s.id !== id);
82      await storage.setItem(SOURCES_KEY, JSON.stringify(filtered));
83    });
84  }
85
86  async touch(id: SourceId): Promise<void> {
87    return withLock(async () => {
88      const sources = await this.getAll();
89      const source = sources.find((s) => s.id === id);
90      if (!source) return;
91      source.lastUsedAt = Date.now();
92      await storage.setItem(SOURCES_KEY, JSON.stringify(sources));
93    });
94  }
95}
