183export default function ManageBooksScreen(): JSX.Element {
184  const { t } = useTranslation();
185  const theme = useTheme();
186  const colors = getColors(theme);
187  const insets = useSafeAreaInsets();
188  const isDark = theme === "dark";
189  const textColor = isDark ? "#FFFFFF" : "#000000";
190  const { sources, createSource, renameSource, deleteSource } = useSources();
191  const { recipes, deleteRecipe } = useRecipes();
192  const isDeletingRef = useRef(false);
193
194  const [isAddingBook, setIsAddingBook] = useState(false);
195  const [newBookName, setNewBookName] = useState("");
196  const [editingSourceId, setEditingSourceId] = useState<SourceId | null>(null);
197  const [editText, setEditText] = useState("");
198
199  const recipeCounts = useMemo(() => {
200    const counts = new Map<SourceId, number>();
201    for (const recipe of recipes) {
202      const src = recipe.metadata.source;
203      if (src && !isUrl(src)) {
204        counts.set(src, (counts.get(src) ?? 0) + 1);
205      }
206    }
207    return counts;
208  }, [recipes]);
209
210  const handleStartAdd = useCallback(() => {
211    setIsAddingBook(true);
212    setNewBookName("");
213  }, []);
214
215  const handleCancelAdd = useCallback(() => {
216    setIsAddingBook(false);
217    setNewBookName("");
218  }, []);
219
220  const handleConfirmAdd = useCallback(async () => {
221    const trimmed = newBookName.trim();
222    if (!trimmed) return;
223    try {
224      await createSource(trimmed);
225      setIsAddingBook(false);
226      setNewBookName("");
227    } catch (error) {
228      const message = error instanceof Error ? error.message : "";
229      if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
230        console.warn("Unexpected error adding book:", error);
231      }
232    }
233  }, [newBookName, createSource]);
234
235  const handleDeleteBook = useCallback(
236    (sourceId: SourceId) => {
237      if (isDeletingRef.current) return;
238      const source = sources.find((s) => s.id === sourceId);
239      if (!source) return;
240      const count = recipeCounts.get(sourceId) ?? 0;
241
242      Alert.alert(
243        t("manageBooks.deleteConfirmTitle", { name: source.name }),
244        t("manageBooks.deleteConfirmMessage", { count }),
245        [
246          { text: t("manageBooks.deleteConfirmCancel"), style: "cancel" },
247          {
248            text: t("manageBooks.deleteConfirmAction"),
249            style: "destructive",
250            onPress: async () => {
251              isDeletingRef.current = true;
252              try {
253                if (Platform.OS !== "web") {
254                  Haptics.notificationAsync(
255                    Haptics.NotificationFeedbackType.Warning
256                  );
257                }
258                const recipesToDelete = recipes.filter(
259                  (r) => r.metadata.source === sourceId
260                );
261                for (const r of recipesToDelete) {
262                  await deleteRecipe(r.id);
263                }
264                LayoutAnimation.configureNext(
265                  LayoutAnimation.Presets.easeInEaseOut
266                );
267                await deleteSource(sourceId);
268              } finally {
269                isDeletingRef.current = false;
270              }
271            },
272          },
273        ]
274      );
275    },
276    [sources, recipeCounts, recipes, deleteRecipe, deleteSource, t]
277  );
278
279  const handleStartEdit = useCallback(
280    (sourceId: SourceId) => {
281      const source = sources.find((s) => s.id === sourceId);
282      if (!source) return;
283      setEditingSourceId(sourceId);
284      setEditText(source.name);
285    },
