    padding: 0,
    margin: 0,
  },
  separator: {
    height: StyleSheet.hairlineWidth,
    marginLeft: 28,
  },
  actionBackground: {
    ...StyleSheet.absoluteFillObject,
    justifyContent: "center",
    paddingHorizontal: 28,
  },
  deleteBackground: {
    backgroundColor: "#FF3B30",
    alignItems: "flex-end",
  },
  editBackground: {
    backgroundColor: "#007AFF",
    alignItems: "flex-start",
  },
  emptyState: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  emptyStateText: {
    fontSize: 17,
  },
  keyboardAvoid: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
  },
  bottomBar: {
    flexDirection: "row",
    alignItems: "center",
    gap: 12,
    paddingHorizontal: 28,
    paddingBottom: 28,
  },
  addInput: {
    flex: 1,
  },
  addInputField: {
    height: 48,
  },
  spacer: {
    flex: 1,
  },
});

