const SWIPE_THRESHOLD = 60;
const ACTION_BUTTON_WIDTH = 80;
const BUTTON_BORDER_RADIUS = 14;

function SwipeableBookRow({
  source,
  recipeCount,
  editingSourceId,
  onDelete,
  onStartEdit,
  onConfirmEdit,
  onCancelEdit,
  editText,
  onEditTextChange,
}: {
  source: Source;
  recipeCount: number;
  editingSourceId: SourceId | null;
  onDelete: (sourceId: SourceId) => void;
  onStartEdit: (sourceId: SourceId) => void;
  onConfirmEdit: () => void;
  onCancelEdit: () => void;
  editText: string;
  onEditTextChange: (text: string) => void;
}): JSX.Element {
  const { t } = useTranslation();
  const theme = useTheme();
  const colors = getColors(theme);
  const isDark = theme === "dark";
  const textColor = isDark ? "#FFFFFF" : "#000000";
  const translateX = useSharedValue(0);
  const startX = useSharedValue(0);
  const isEditing = editingSourceId === source.id;
  const isEditingShared = useSharedValue(isEditing);

  useEffect(() => {
    isEditingShared.value = isEditing;
    translateX.value = withSpring(isEditing ? ACTION_BUTTON_WIDTH : 0);
  }, [isEditing, isEditingShared, translateX]);

  const triggerDelete = useCallback(() => {
    onDelete(source.id);
  }, [source.id, onDelete]);

  const handleEditPress = useCallback(() => {
    if (isEditing) {
      onConfirmEdit();
    } else {
      onStartEdit(source.id);
    }
  }, [isEditing, onConfirmEdit, onStartEdit, source.id]);

  const pan = Gesture.Pan()
    .activeOffsetX([-10, 10])
    .failOffsetY([-10, 10])
    .onStart(() => {
      startX.value = translateX.value;
    })
    .onUpdate((event) => {
      translateX.value = startX.value + event.translationX;
    })
    .onEnd(() => {
      if (isEditingShared.value) {
        if (translateX.value < SWIPE_THRESHOLD / 2) {
          translateX.value = withSpring(0);
          runOnJS(onCancelEdit)();
        } else {
          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
        }
      } else {
        if (translateX.value < -SWIPE_THRESHOLD) {
          translateX.value = withSpring(-ACTION_BUTTON_WIDTH);
        } else if (translateX.value > SWIPE_THRESHOLD) {
          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
        } else {
          translateX.value = withSpring(0);
        }
      }
    });

  const tap = Gesture.Tap().onEnd(() => {
    if (Math.abs(translateX.value) > 10 && !isEditingShared.value) {
      translateX.value = withSpring(0);
    }
  });

  const gesture = Gesture.Race(pan, tap);

  const contentAnimStyle = useAnimatedStyle(() => ({
    transform: [{ translateX: translateX.value }],
  }));

  const contentBgStyle = useAnimatedStyle(() => {
    const absX = Math.abs(translateX.value);
    const progress = Math.min(absX / SWIPE_THRESHOLD, 1);
    const bgColor = interpolateColor(
      progress,
      [0, 1],
      ["rgba(128,128,128,0)", "rgba(128,128,128,0.12)"]
    );
    return {
      backgroundColor: bgColor,
      borderRadius: interpolate(progress, [0, 1], [0, 10]),
    };
  });

  const editButtonStyle = useAnimatedStyle(() => ({
    width: translateX.value > 0 ? translateX.value : 0,
    opacity: translateX.value > 0 ? 1 : 0,
  }));

  const deleteButtonStyle = useAnimatedStyle(() => ({
    width: translateX.value < 0 ? -translateX.value : 0,
    opacity: translateX.value < 0 ? 1 : 0,
  }));

  return (
    <View style={styles.rowContainer}>
      {/* Edit button - left */}
      <Animated.View
        style={[
          styles.actionButtonContainer,
          styles.editButtonContainer,
          editButtonStyle,
        ]}
      >
        <Pressable
          onPress={handleEditPress}
          style={[styles.actionButton, styles.editButton]}
        >
          <MaterialIcons
            name={isEditing ? "check" : "edit"}
            size={22}
            color="#FFFFFF"
          />
        </Pressable>
      </Animated.View>

      {/* Delete button - right */}
      <Animated.View
        style={[
          styles.actionButtonContainer,
          styles.deleteButtonContainer,
          deleteButtonStyle,
        ]}
      >
        <Pressable
          onPress={triggerDelete}
          style={[styles.actionButton, styles.deleteButton]}
        >
          <MaterialIcons name="delete" size={22} color="#FFFFFF" />
        </Pressable>
      </Animated.View>

      {/* Content */}
      <GestureDetector gesture={gesture}>
        <Animated.View style={[styles.listItem, contentAnimStyle, contentBgStyle]}>
          {isEditing ? (
            <>
              <TextInput
                style={[styles.editInput, { color: textColor }]}
                value={editText}
                onChangeText={onEditTextChange}
                maxLength={100}
                autoFocus
                selectTextOnFocus
                returnKeyType="done"
                onSubmitEditing={onConfirmEdit}
              />
              <Text
                style={[styles.recipeCount, { color: colors.textSecondary }]}
              >
                {t("manageBooks.recipeCount", { count: recipeCount })}
              </Text>
            </>
          ) : (
            <>
              <Text style={[styles.bookTitle, { color: textColor }]}>
                {source.name}
              </Text>
              <Text
                style={[styles.recipeCount, { color: colors.textSecondary }]}
              >
                {t("manageBooks.recipeCount", { count: recipeCount })}
              </Text>
            </>
          )}
        </Animated.View>
      </GestureDetector>

      <View
        style={[styles.separator, { backgroundColor: colors.separator }]}
      />
    </View>
  );
}

