233export default function ManageBooksScreen(): JSX.Element {
234  const { t } = useTranslation();
235  const theme = useTheme();
236  const colors = getColors(theme);
237  const insets = useSafeAreaInsets();
238  const isDark = theme === "dark";
239  const textColor = isDark ? "#FFFFFF" : "#000000";
240  const { sources, createSource, renameSource, deleteSource } = useSources();
241  const { recipes, deleteRecipe } = useRecipes();
242  const isDeletingRef = useRef(false);
243
244  const [isAddingBook, setIsAddingBook] = useState(false);
245  const [newBookName, setNewBookName] = useState("");
246  const [editingSourceId, setEditingSourceId] = useState<SourceId | null>(null);
247  const [editText, setEditText] = useState("");
248
249  const recipeCounts = useMemo(() => {
250    const counts = new Map<SourceId, number>();
251    for (const recipe of recipes) {
252      const src = recipe.metadata.source;
253      if (src && !isUrl(src)) {
254        counts.set(src, (counts.get(src) ?? 0) + 1);
255      }
256    }
257    return counts;
258  }, [recipes]);
259
260  const handleStartAdd = useCallback(() => {
261    setIsAddingBook(true);
262    setNewBookName("");
263  }, []);
264
265  const handleCancelAdd = useCallback(() => {
266    setIsAddingBook(false);
267    setNewBookName("");
268  }, []);
269
270  const handleConfirmAdd = useCallback(async () => {
271    const trimmed = newBookName.trim();
272    if (!trimmed) return;
273    try {
274      await createSource(trimmed);
275      setIsAddingBook(false);
276      setNewBookName("");
277    } catch (error) {
278      const message = error instanceof Error ? error.message : "";
279      if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
280        console.warn("Unexpected error adding book:", error);
281      }
282    }
283  }, [newBookName, createSource]);
284
285  const handleDeleteBook = useCallback(
286    (sourceId: SourceId) => {
287      if (isDeletingRef.current) return;
288      const source = sources.find((s) => s.id === sourceId);
289      if (!source) return;
290      const count = recipeCounts.get(sourceId) ?? 0;
291
292      Alert.alert(
293        t("manageBooks.deleteConfirmTitle", { name: source.name }),
294        t("manageBooks.deleteConfirmMessage", { count }),
295        [
296          { text: t("manageBooks.deleteConfirmCancel"), style: "cancel" },
297          {
298            text: t("manageBooks.deleteConfirmAction"),
299            style: "destructive",
300            onPress: async () => {
301              isDeletingRef.current = true;
302              try {
303                if (Platform.OS !== "web") {
304                  Haptics.notificationAsync(
305                    Haptics.NotificationFeedbackType.Warning
306                  );
307                }
308                const recipesToDelete = recipes.filter(
309                  (r) => r.metadata.source === sourceId
310                );
311                for (const r of recipesToDelete) {
312                  await deleteRecipe(r.id);
313                }
314                LayoutAnimation.configureNext(
315                  LayoutAnimation.Presets.easeInEaseOut
316                );
317                await deleteSource(sourceId);
318              } finally {
319                isDeletingRef.current = false;
320              }
321            },
322          },
323        ]
324      );
325    },
326    [sources, recipeCounts, recipes, deleteRecipe, deleteSource, t]
327  );
328
329  const handleStartEdit = useCallback(
330    (sourceId: SourceId) => {
331      const source = sources.find((s) => s.id === sourceId);
332      if (!source) return;
333      setEditingSourceId(sourceId);
334      setEditText(source.name);
335    },
336    [sources]
337  );
338
339  const handleConfirmEdit = useCallback(async () => {
340    if (!editingSourceId) return;
341    const trimmed = editText.trim();
342    if (trimmed) {
343      try {
344        await renameSource(editingSourceId, trimmed);
345      } catch (error) {
346        const message = error instanceof Error ? error.message : "";
347        if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
348          console.warn("Unexpected error renaming book:", error);
349        }
350      }
351    }
352    setEditingSourceId(null);
353    setEditText("");
354  }, [editingSourceId, editText, renameSource]);
355
356  const handleCancelEdit = useCallback(() => {
357    setEditingSourceId(null);
358    setEditText("");
359  }, []);
360
361  return (
362    <View style={[styles.container, { backgroundColor: colors.background }]}>
363      <View style={[styles.header, { paddingTop: insets.top + 16 }]}>
364        <Text style={[styles.headerTitle, { color: textColor }]}>
365          {t("manageBooks.title")}
366        </Text>
367      </View>
368
369      {sources.length === 0 && !isAddingBook ? (
370        <View style={styles.emptyState}>
371          <Text style={[styles.emptyStateText, { color: colors.textSecondary }]}>
372            {t("manageBooks.emptyState")}
373          </Text>
374        </View>
375      ) : (
376        <ScrollView
377          style={styles.list}
378          contentContainerStyle={styles.listContent}
379        >
380          {sources.map((source) => (
381            <SwipeableBookRow
382              key={source.id}
383              source={source}
384              recipeCount={recipeCounts.get(source.id) ?? 0}
385              editingSourceId={editingSourceId}
386              onDelete={handleDeleteBook}
387              onStartEdit={handleStartEdit}
388              onConfirmEdit={handleConfirmEdit}
389              onCancelEdit={handleCancelEdit}
390              editText={editText}
391              onEditTextChange={setEditText}
392            />
393          ))}
394        </ScrollView>
395      )}
396
397      <KeyboardAvoidingView
398        behavior={Platform.OS === "ios" ? "padding" : "height"}
399        style={styles.keyboardAvoid}
400      >
401        <View style={styles.bottomBar}>
402          {isAddingBook ? (
403            <>
404              <LiquidGlassButton
405                onPress={handleCancelAdd}
406                systemImage="xmark"
407                accessibilityLabel={t("manageBooks.deleteConfirmCancel")}
408              />
409              <View style={styles.addInput}>
410                <LiquidGlassInput
411                  value={newBookName}
412                  onChangeText={setNewBookName}
413                  placeholder={t("sourceSelector.addNewPlaceholder")}
414                  variant="form"
415                  style={styles.addInputField}
416                  autoFocus
417                  maxLength={100}
418                  returnKeyType="done"
419                  onSubmitEditing={handleConfirmAdd}
420                />
421              </View>
422              <LiquidGlassButton
423                onPress={handleConfirmAdd}
424                systemImage="checkmark"
425                accessibilityLabel={t("accessibility.confirm")}
426              />
427            </>
428          ) : (
429            <>
430              <LiquidGlassButton
431                onPress={router.back}
432                systemImage="chevron.left"
433                accessibilityLabel={t("manageBooks.back")}
434              />
435              <View style={styles.spacer} />
436              <LiquidGlassButton
437                onPress={handleStartAdd}
438                systemImage="plus"
439                accessibilityLabel={t("manageBooks.addBook")}
440              />
441            </>
442          )}
443        </View>
444      </KeyboardAvoidingView>
445    </View>
446  );
447}
448
449const styles = StyleSheet.create({
450  container: {
451    flex: 1,
452  },
453  header: {
454    paddingHorizontal: 28,
455    paddingBottom: 16,
456  },
457  headerTitle: {
458    fontSize: 34,
459    fontWeight: "bold",
460  },
461  list: {
462    flex: 1,
463  },
464  listContent: {
465    paddingBottom: 120,
466  },
