+
+const SWIPE_THRESHOLD = 80;
+
+function SwipeableBookRow({
+  source,
+  recipeCount,
+  editingSourceId,
+  onDelete,
+  onStartEdit,
+  onConfirmEdit,
+  onCancelEdit,
+  editText,
+  onEditTextChange,
+}: {
+  source: Source;
+  recipeCount: number;
+  editingSourceId: SourceId | null;
+  onDelete: (sourceId: SourceId) => void;
+  onStartEdit: (sourceId: SourceId) => void;
+  onConfirmEdit: () => void;
+  onCancelEdit: () => void;
+  editText: string;
+  onEditTextChange: (text: string) => void;
+}): JSX.Element {
+  const { t } = useTranslation();
+  const theme = useTheme();
+  const colors = getColors(theme);
+  const isDark = theme === "dark";
+  const textColor = isDark ? "#FFFFFF" : "#000000";
+  const translateX = useSharedValue(0);
+  const isEditing = editingSourceId === source.id;
+
+  const triggerDelete = useCallback(() => {
+    onDelete(source.id);
+  }, [source.id, onDelete]);
+
+  const triggerEdit = useCallback(() => {
+    onStartEdit(source.id);
+  }, [source.id, onStartEdit]);
+
+  const pan = Gesture.Pan()
+    .activeOffsetX([-10, 10])
+    .failOffsetY([-10, 10])
+    .onUpdate((event) => {
+      translateX.value = event.translationX;
+    })
+    .onEnd((event) => {
+      translateX.value = withSpring(0);
+      if (event.translationX < -SWIPE_THRESHOLD) {
+        runOnJS(triggerDelete)();
+      } else if (event.translationX > SWIPE_THRESHOLD) {
+        runOnJS(triggerEdit)();
+      }
+    });
+
+  const cancelPan = Gesture.Pan()
+    .activeOffsetX([-10, 10])
+    .failOffsetY([-10, 10])
+    .onEnd((event) => {
+      if (event.translationX > SWIPE_THRESHOLD) {
+        runOnJS(onCancelEdit)();
+      }
+    });
+
+  const contentStyle = useAnimatedStyle(() => ({
+    transform: [{ translateX: translateX.value }],
+  }));
+
+  const deleteBackgroundStyle = useAnimatedStyle(() => ({
+    opacity: translateX.value < 0 ? Math.min(1, -translateX.value / SWIPE_THRESHOLD) : 0,
+  }));
+
+  const editBackgroundStyle = useAnimatedStyle(() => ({
+    opacity: translateX.value > 0 ? Math.min(1, translateX.value / SWIPE_THRESHOLD) : 0,
+  }));
+
