  1import { useRecipes } from "@/features/recipes-list/context/RecipesContext";
  2import { useSources } from "@/features/sources/context/SourcesContext";
  3import { Alert } from "@/lib/alert";
  4import type { SourceId } from "@/lib/types/primitives";
  5import type { Source } from "@/lib/types/source";
  6import { isUrl } from "@/lib/utils/recipeValidation";
  7import { LiquidGlassButton, LiquidGlassInput } from "@/modules/liquid-glass";
  8import { useTranslation } from "@/platform/i18n/useTranslation";
  9import { getColors } from "@/platform/theme/glassStyles";
 10import { useTheme } from "@/platform/theme/useTheme";
 11import { MaterialIcons } from "@expo/vector-icons";
 12import * as Haptics from "expo-haptics";
 13import { router } from "expo-router";
 14import { useCallback, useEffect, useMemo, useRef, useState, type JSX } from "react";
 15import {
 16  KeyboardAvoidingView,
 17  LayoutAnimation,
 18  Platform,
 19  Pressable,
 20  ScrollView,
 21  StyleSheet,
 22  Text,
 23  TextInput,
 24  View,
 25} from "react-native";
 26import { Gesture, GestureDetector } from "react-native-gesture-handler";
 27import Animated, {
 28  interpolate,
 29  interpolateColor,
 30  runOnJS,
 31  useAnimatedStyle,
 32  useSharedValue,
 33  withSpring,
 34} from "react-native-reanimated";
 35import { useSafeAreaInsets } from "react-native-safe-area-context";
 36
 37const SWIPE_THRESHOLD = 60;
 38const ACTION_BUTTON_WIDTH = 80;
 39
 40function SwipeableBookRow({
 41  source,
 42  recipeCount,
 43  editingSourceId,
 44  onDelete,
 45  onStartEdit,
 46  onConfirmEdit,
 47  onCancelEdit,
 48  editText,
 49  onEditTextChange,
 50}: {
 51  source: Source;
 52  recipeCount: number;
 53  editingSourceId: SourceId | null;
 54  onDelete: (sourceId: SourceId) => void;
 55  onStartEdit: (sourceId: SourceId) => void;
 56  onConfirmEdit: () => void;
 57  onCancelEdit: () => void;
 58  editText: string;
 59  onEditTextChange: (text: string) => void;
 60}): JSX.Element {
 61  const { t } = useTranslation();
 62  const theme = useTheme();
 63  const colors = getColors(theme);
 64  const isDark = theme === "dark";
 65  const textColor = isDark ? "#FFFFFF" : "#000000";
 66  const translateX = useSharedValue(0);
 67  const startX = useSharedValue(0);
 68  const isEditing = editingSourceId === source.id;
 69  const isEditingShared = useSharedValue(isEditing);
 70
 71  isEditingShared.value = isEditing;
 72
 73  useEffect(() => {
 74    translateX.value = withSpring(isEditing ? ACTION_BUTTON_WIDTH : 0);
 75  }, [isEditing, translateX]);
 76
 77  const triggerDelete = useCallback(() => {
 78    translateX.value = withSpring(0);
 79    onDelete(source.id);
 80  }, [source.id, onDelete, translateX]);
 81
 82  const handleEditPress = useCallback(() => {
 83    if (isEditing) {
 84      onConfirmEdit();
 85    } else {
 86      onStartEdit(source.id);
 87    }
 88  }, [isEditing, onConfirmEdit, onStartEdit, source.id]);
 89
 90  const pan = Gesture.Pan()
 91    .activeOffsetX([-10, 10])
 92    .failOffsetY([-10, 10])
 93    .onStart(() => {
 94      startX.value = translateX.value;
 95    })
 96    .onUpdate((event) => {
 97      const raw = startX.value + event.translationX;
 98      const limit = ACTION_BUTTON_WIDTH * 1.5;
 99      translateX.value = Math.max(-limit, Math.min(limit, raw));
100    })
101    .onEnd(() => {
102      if (isEditingShared.value) {
103        if (translateX.value < SWIPE_THRESHOLD / 2) {
104          translateX.value = withSpring(0);
105          runOnJS(onCancelEdit)();
106        } else {
107          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
108        }
109      } else {
110        if (translateX.value < -SWIPE_THRESHOLD) {
111          translateX.value = withSpring(-ACTION_BUTTON_WIDTH);
112        } else if (translateX.value > SWIPE_THRESHOLD) {
113          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
114        } else {
115          translateX.value = withSpring(0);
116        }
117      }
118    });
119
120  const tap = Gesture.Tap().onEnd(() => {
121    if (Math.abs(translateX.value) > 10 && !isEditingShared.value) {
122      translateX.value = withSpring(0);
123    }
124  });
125
126  const gesture = Gesture.Race(pan, tap);
127
128  const contentAnimStyle = useAnimatedStyle(() => ({
129    transform: [{ translateX: translateX.value }],
130  }));
131
132  const contentBgStyle = useAnimatedStyle(() => {
133    const absX = Math.abs(translateX.value);
134    const progress = Math.min(absX / SWIPE_THRESHOLD, 1);
135    const bgColor = interpolateColor(
136      progress,
137      [0, 1],
138      ["rgba(128,128,128,0)", "rgba(128,128,128,0.12)"]
139    );
140    return {
141      backgroundColor: bgColor,
142      borderRadius: interpolate(progress, [0, 1], [0, 10]),
143    };
144  });
145
146  const editButtonStyle = useAnimatedStyle(() => ({
147    width: translateX.value > 0 ? translateX.value : 0,
148    opacity: translateX.value > 0 ? 1 : 0,
149  }));
150
151  const deleteButtonStyle = useAnimatedStyle(() => ({
152    width: translateX.value < 0 ? -translateX.value : 0,
153    opacity: translateX.value < 0 ? 1 : 0,
154  }));
155
156  return (
157    <View style={styles.rowContainer}>
158      {/* Edit button - left */}
159      <Animated.View
160        style={[
161          styles.actionButtonContainer,
162          styles.editButtonContainer,
163          editButtonStyle,
164        ]}
165      >
166        <Pressable
167          onPress={handleEditPress}
168          style={[styles.actionButton, styles.editButton]}
169        >
170          <MaterialIcons
171            name={isEditing ? "check" : "edit"}
172            size={22}
173            color="#FFFFFF"
174          />
175        </Pressable>
176      </Animated.View>
177
178      {/* Delete button - right */}
179      <Animated.View
180        style={[
181          styles.actionButtonContainer,
182          styles.deleteButtonContainer,
183          deleteButtonStyle,
184        ]}
185      >
186        <Pressable
187          onPress={triggerDelete}
188          style={[styles.actionButton, styles.deleteButton]}
189        >
190          <MaterialIcons name="delete" size={22} color="#FFFFFF" />
191        </Pressable>
192      </Animated.View>
193
194      {/* Content */}
195      <GestureDetector gesture={gesture}>
196        <Animated.View style={[styles.listItem, contentAnimStyle, contentBgStyle]}>
197          {isEditing ? (
198            <>
199              <TextInput
200                style={[styles.editInput, { color: textColor }]}
201                value={editText}
202                onChangeText={onEditTextChange}
203                maxLength={100}
204                autoFocus
205                selectTextOnFocus
206                returnKeyType="done"
207                onSubmitEditing={onConfirmEdit}
208              />
209              <Text
210                style={[styles.recipeCount, { color: colors.textSecondary }]}
211              >
212                {t("manageBooks.recipeCount", { count: recipeCount })}
213              </Text>
214            </>
215          ) : (
216            <>
217              <Text style={[styles.bookTitle, { color: textColor }]}>
218                {source.name}
219              </Text>
220              <Text
221                style={[styles.recipeCount, { color: colors.textSecondary }]}
222              >
223                {t("manageBooks.recipeCount", { count: recipeCount })}
224              </Text>
225            </>
226          )}
227        </Animated.View>
228      </GestureDetector>
229
230      <View
231        style={[styles.separator, { backgroundColor: colors.separator }]}
232      />
233    </View>
234  );
235}
