237export default function ManageBooksScreen(): JSX.Element {
238  const { t } = useTranslation();
239  const theme = useTheme();
240  const colors = getColors(theme);
241  const insets = useSafeAreaInsets();
242  const isDark = theme === "dark";
243  const textColor = isDark ? "#FFFFFF" : "#000000";
244  const { sources, createSource, renameSource, deleteSource } = useSources();
245  const { recipes, deleteRecipe } = useRecipes();
246  const isDeletingRef = useRef(false);
247
248  const [isAddingBook, setIsAddingBook] = useState(false);
249  const [newBookName, setNewBookName] = useState("");
250  const [editingSourceId, setEditingSourceId] = useState<SourceId | null>(null);
251  const [editText, setEditText] = useState("");
252
253  const recipeCounts = useMemo(() => {
254    const counts = new Map<SourceId, number>();
255    for (const recipe of recipes) {
256      const src = recipe.metadata.source;
257      if (src && !isUrl(src)) {
258        counts.set(src, (counts.get(src) ?? 0) + 1);
259      }
260    }
261    return counts;
262  }, [recipes]);
263
264  const handleStartAdd = useCallback(() => {
265    setIsAddingBook(true);
266    setNewBookName("");
267  }, []);
268
269  const handleCancelAdd = useCallback(() => {
270    setIsAddingBook(false);
271    setNewBookName("");
272  }, []);
273
274  const handleConfirmAdd = useCallback(async () => {
275    const trimmed = newBookName.trim();
276    if (!trimmed) return;
277    try {
278      await createSource(trimmed);
279      setIsAddingBook(false);
280      setNewBookName("");
281    } catch (error) {
282      const message = error instanceof Error ? error.message : "";
283      if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
284        console.warn("Unexpected error adding book:", error);
285      }
286    }
287  }, [newBookName, createSource]);
288
289  const handleDeleteBook = useCallback(
290    (sourceId: SourceId) => {
291      if (isDeletingRef.current) return;
292      const source = sources.find((s) => s.id === sourceId);
293      if (!source) return;
294      const count = recipeCounts.get(sourceId) ?? 0;
295
296      Alert.alert(
297        t("manageBooks.deleteConfirmTitle", { name: source.name }),
298        t("manageBooks.deleteConfirmMessage", { count }),
299        [
300          { text: t("manageBooks.deleteConfirmCancel"), style: "cancel" },
301          {
302            text: t("manageBooks.deleteConfirmAction"),
303            style: "destructive",
304            onPress: async () => {
305              isDeletingRef.current = true;
306              try {
307                if (Platform.OS !== "web") {
308                  Haptics.notificationAsync(
309                    Haptics.NotificationFeedbackType.Warning
310                  );
311                }
312                const recipesToDelete = recipes.filter(
313                  (r) => r.metadata.source === sourceId
314                );
315                for (const r of recipesToDelete) {
316                  await deleteRecipe(r.id);
317                }
318                LayoutAnimation.configureNext(
319                  LayoutAnimation.Presets.easeInEaseOut
320                );
321                await deleteSource(sourceId);
322              } finally {
323                isDeletingRef.current = false;
324              }
325            },
326          },
327        ]
328      );
329    },
330    [sources, recipeCounts, recipes, deleteRecipe, deleteSource, t]
331  );
332
333  const handleStartEdit = useCallback(
334    (sourceId: SourceId) => {
335      const source = sources.find((s) => s.id === sourceId);
336      if (!source) return;
337      setEditingSourceId(sourceId);
338      setEditText(source.name);
339    },
340    [sources]
341  );
342
343  const handleConfirmEdit = useCallback(async () => {
344    if (!editingSourceId) return;
345    const trimmed = editText.trim();
346    if (trimmed) {
347      try {
348        await renameSource(editingSourceId, trimmed);
349      } catch (error) {
350        const message = error instanceof Error ? error.message : "";
351        if (!message.includes("already exists") && !message.includes("cannot be empty") && !message.includes("too long")) {
352          console.warn("Unexpected error renaming book:", error);
353        }
354      }
355    }
356    setEditingSourceId(null);
357    setEditText("");
358  }, [editingSourceId, editText, renameSource]);
359
360  const handleCancelEdit = useCallback(() => {
361    setEditingSourceId(null);
362    setEditText("");
363  }, []);
364
365  return (
366    <View style={[styles.container, { backgroundColor: colors.background }]}>
