  if (w < BUTTON_SCALE_THRESHOLD) {
    const s = (w - SCALE_MIN_WIDTH) / (BUTTON_SCALE_THRESHOLD - SCALE_MIN_WIDTH);
    return { transform: [{ scale: s }], opacity: s };
  }
  return { transform: [{ scale: 1 }], opacity: 1 };
});

const deleteScaleStyle = useAnimatedStyle(() => {
  const w = Math.max(-translateX.value - BUTTON_GAP, 0);
  if (w <= SCALE_MIN_WIDTH) {
    return { transform: [{ scale: 0 }], opacity: 0 };
  }
  if (w < BUTTON_SCALE_THRESHOLD) {
    const s = (w - SCALE_MIN_WIDTH) / (BUTTON_SCALE_THRESHOLD - SCALE_MIN_WIDTH);
    return { transform: [{ scale: s }], opacity: s };
  }
  return { transform: [{ scale: 1 }], opacity: 1 };
});

return (
  <View style={styles.rowContainer}>
    {/* Edit button - left */}
    <Animated.View
      style={[
        styles.actionButtonContainer,
        styles.editButtonContainer,
        editButtonWidthStyle,
      ]}
    >
      <Pressable onPress={handleEditPress} style={styles.actionButtonPressable}>
        <Animated.View style={[styles.actionButtonContent, editScaleStyle]}>
          <View style={[styles.actionButtonIcon, styles.editButtonColor]}>
            <MaterialIcons
              name={isEditing ? "check" : "edit"}
              size={20}
              color="#FFFFFF"
            />
          </View>
          <Text style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
            {isEditing
              ? t("manageBooks.confirmEditAction")
              : t("manageBooks.editAction")}
          </Text>
        </Animated.View>
      </Pressable>
    </Animated.View>

    {/* Delete button - right */}
    <Animated.View
      style={[
        styles.actionButtonContainer,
