  1import { useRecipes } from "@/features/recipes-list/context/RecipesContext";
  2import { useSources } from "@/features/sources/context/SourcesContext";
  3import { Alert } from "@/lib/alert";
  4import type { SourceId } from "@/lib/types/primitives";
  5import type { Source } from "@/lib/types/source";
  6import { isUrl } from "@/lib/utils/recipeValidation";
  7import { LiquidGlassButton, LiquidGlassInput } from "@/modules/liquid-glass";
  8import { useTranslation } from "@/platform/i18n/useTranslation";
  9import { getColors } from "@/platform/theme/glassStyles";
 10import { useTheme } from "@/platform/theme/useTheme";
 11import { MaterialIcons } from "@expo/vector-icons";
 12import * as Haptics from "expo-haptics";
 13import { router } from "expo-router";
 14import { useCallback, useEffect, useMemo, useRef, useState, type JSX } from "react";
 15import {
 16  KeyboardAvoidingView,
 17  LayoutAnimation,
 18  Platform,
 19  Pressable,
 20  ScrollView,
 21  StyleSheet,
 22  Text,
 23  TextInput,
 24  View,
 25} from "react-native";
 26import { Gesture, GestureDetector } from "react-native-gesture-handler";
 27import Animated, {
 28  interpolate,
 29  interpolateColor,
 30  runOnJS,
 31  useAnimatedStyle,
 32  useSharedValue,
 33  withSpring,
 34} from "react-native-reanimated";
 35import { useSafeAreaInsets } from "react-native-safe-area-context";
 36
 37const SWIPE_THRESHOLD = 60;
 38const ACTION_BUTTON_WIDTH = 80;
 39const BUTTON_GAP = 10;
 40const BUTTON_SCALE_THRESHOLD = 56;
 41const SCALE_MIN_WIDTH = 10;
 42
 43function SwipeableBookRow({
 44  source,
 45  recipeCount,
 46  editingSourceId,
 47  onDelete,
 48  onStartEdit,
 49  onConfirmEdit,
 50  onCancelEdit,
 51  editText,
 52  onEditTextChange,
 53}: {
 54  source: Source;
 55  recipeCount: number;
 56  editingSourceId: SourceId | null;
 57  onDelete: (sourceId: SourceId) => void;
 58  onStartEdit: (sourceId: SourceId) => void;
 59  onConfirmEdit: () => void;
 60  onCancelEdit: () => void;
 61  editText: string;
 62  onEditTextChange: (text: string) => void;
 63}): JSX.Element {
 64  const { t } = useTranslation();
 65  const theme = useTheme();
 66  const colors = getColors(theme);
 67  const isDark = theme === "dark";
 68  const textColor = isDark ? "#FFFFFF" : "#000000";
 69  const translateX = useSharedValue(0);
 70  const startX = useSharedValue(0);
 71  const isEditing = editingSourceId === source.id;
 72  const isEditingShared = useSharedValue(isEditing);
 73
 74  isEditingShared.value = isEditing;
 75
 76  useEffect(() => {
 77    translateX.value = withSpring(isEditing ? ACTION_BUTTON_WIDTH : 0);
 78  }, [isEditing, translateX]);
 79
 80  const triggerDelete = useCallback(() => {
 81    translateX.value = withSpring(0);
 82    onDelete(source.id);
 83  }, [source.id, onDelete, translateX]);
 84
 85  const handleEditPress = useCallback(() => {
 86    if (isEditing) {
 87      onConfirmEdit();
 88    } else {
 89      onStartEdit(source.id);
 90    }
 91  }, [isEditing, onConfirmEdit, onStartEdit, source.id]);
 92
 93  const pan = Gesture.Pan()
 94    .activeOffsetX([-10, 10])
 95    .failOffsetY([-10, 10])
 96    .onStart(() => {
 97      startX.value = translateX.value;
 98    })
 99    .onUpdate((event) => {
100      const raw = startX.value + event.translationX;
101      const limit = ACTION_BUTTON_WIDTH * 1.5;
102      translateX.value = Math.max(-limit, Math.min(limit, raw));
103    })
104    .onEnd(() => {
105      if (isEditingShared.value) {
106        if (translateX.value < SWIPE_THRESHOLD / 2) {
107          translateX.value = withSpring(0);
108          runOnJS(onCancelEdit)();
109        } else {
110          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
111        }
112      } else {
113        if (translateX.value < -SWIPE_THRESHOLD) {
114          translateX.value = withSpring(-ACTION_BUTTON_WIDTH);
115        } else if (translateX.value > SWIPE_THRESHOLD) {
116          translateX.value = withSpring(ACTION_BUTTON_WIDTH);
117        } else {
118          translateX.value = withSpring(0);
119        }
120      }
121    });
122
123  const tap = Gesture.Tap().onEnd(() => {
124    if (Math.abs(translateX.value) > 10 && !isEditingShared.value) {
125      translateX.value = withSpring(0);
126    }
127  });
128
129  const gesture = Gesture.Race(pan, tap);
130
131  const contentAnimStyle = useAnimatedStyle(() => ({
132    transform: [{ translateX: translateX.value }],
133  }));
134
135  const contentBgStyle = useAnimatedStyle(() => {
136    const absX = Math.abs(translateX.value);
137    const progress = Math.min(absX / SWIPE_THRESHOLD, 1);
138    const bgColor = interpolateColor(
139      progress,
140      [0, 1],
141      ["rgba(128,128,128,0)", "rgba(128,128,128,0.12)"]
142    );
143    return {
144      backgroundColor: bgColor,
145      borderRadius: interpolate(progress, [0, 1], [0, 18]),
146    };
147  });
148
149  const editButtonWidthStyle = useAnimatedStyle(() => ({
150    width: translateX.value > BUTTON_GAP ? translateX.value - BUTTON_GAP : 0,
151    opacity: translateX.value > BUTTON_GAP ? 1 : 0,
152  }));
153
154  const deleteButtonWidthStyle = useAnimatedStyle(() => ({
155    width: -translateX.value > BUTTON_GAP ? -translateX.value - BUTTON_GAP : 0,
156    opacity: -translateX.value > BUTTON_GAP ? 1 : 0,
157  }));
158
159  const editScaleStyle = useAnimatedStyle(() => {
160    const w = Math.max(translateX.value - BUTTON_GAP, 0);
161    if (w <= SCALE_MIN_WIDTH) {
162      return { transform: [{ scale: 0 }], opacity: 0 };
163    }
164    if (w < BUTTON_SCALE_THRESHOLD) {
165      const s = (w - SCALE_MIN_WIDTH) / (BUTTON_SCALE_THRESHOLD - SCALE_MIN_WIDTH);
166      return { transform: [{ scale: s }], opacity: s };
167    }
168    return { transform: [{ scale: 1 }], opacity: 1 };
169  });
170
171  const deleteScaleStyle = useAnimatedStyle(() => {
172    const w = Math.max(-translateX.value - BUTTON_GAP, 0);
173    if (w <= SCALE_MIN_WIDTH) {
174      return { transform: [{ scale: 0 }], opacity: 0 };
175    }
176    if (w < BUTTON_SCALE_THRESHOLD) {
177      const s = (w - SCALE_MIN_WIDTH) / (BUTTON_SCALE_THRESHOLD - SCALE_MIN_WIDTH);
178      return { transform: [{ scale: s }], opacity: s };
179    }
180    return { transform: [{ scale: 1 }], opacity: 1 };
181  });
182
183  return (
184    <View style={styles.rowContainer}>
185      {/* Edit button - left */}
186      <Animated.View
187        style={[
188          styles.actionButtonContainer,
189          styles.editButtonContainer,
190          editButtonWidthStyle,
191        ]}
192      >
193        <Pressable onPress={handleEditPress} style={styles.actionButtonPressable}>
194          <Animated.View style={[styles.actionButtonContent, editScaleStyle]}>
195            <View style={[styles.actionButtonIcon, styles.editButtonColor]}>
196              <MaterialIcons
197                name={isEditing ? "check" : "edit"}
198                size={20}
199                color="#FFFFFF"
200              />
201            </View>
202            <Text style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
203              {isEditing
204                ? t("manageBooks.confirmEditAction")
205                : t("manageBooks.editAction")}
206            </Text>
207          </Animated.View>
208        </Pressable>
209      </Animated.View>
210
211      {/* Delete button - right */}
212      <Animated.View
213        style={[
214          styles.actionButtonContainer,
215          styles.deleteButtonContainer,
216          deleteButtonWidthStyle,
217        ]}
218      >
219        <Pressable onPress={triggerDelete} style={styles.actionButtonPressable}>
220          <Animated.View style={[styles.actionButtonContent, deleteScaleStyle]}>
221            <View style={[styles.actionButtonIcon, styles.deleteButtonColor]}>
222              <MaterialIcons name="delete" size={20} color="#FFFFFF" />
223            </View>
224            <Text style={[styles.actionButtonLabel, { color: colors.textSecondary }]}>
225              {t("manageBooks.deleteConfirmAction")}
226            </Text>
227          </Animated.View>
228        </Pressable>
229      </Animated.View>
230
231      {/* Content */}
232      <GestureDetector gesture={gesture}>
233        <Animated.View style={[styles.listItem, contentAnimStyle, contentBgStyle]}>
234          {isEditing ? (
235            <>
236              <TextInput
237                style={[styles.editInput, { color: textColor }]}
238                value={editText}
239                onChangeText={onEditTextChange}
240                maxLength={100}
241                autoFocus
242                selectTextOnFocus
243                returnKeyType="done"
244                onSubmitEditing={onConfirmEdit}
245              />
246              <Text
247                style={[styles.recipeCount, { color: colors.textSecondary }]}
248              >
249                {t("manageBooks.recipeCount", { count: recipeCount })}
250              </Text>
251            </>
252          ) : (
253            <>
254              <Text style={[styles.bookTitle, { color: textColor }]}>
255                {source.name}
256              </Text>
257              <Text
258                style={[styles.recipeCount, { color: colors.textSecondary }]}
259              >
260                {t("manageBooks.recipeCount", { count: recipeCount })}
261              </Text>
262            </>
263          )}
264        </Animated.View>
265      </GestureDetector>
266
267      <View
268        style={[styles.separator, { backgroundColor: colors.separator }]}
269      />
270    </View>
271  );
272}
