 1→import { useMemo } from "react";
 2→
 3→/**
 4→ * Given source image dimensions and container dimensions,
 5→ * returns the cover-rendered size (may be larger than container on one axis).
 6→ * Returns undefined until source dimensions are known.
 7→ */
 8→export function useCoverSize(
 9→  sourceSize: { width: number; height: number } | null,
10→  containerWidth: number,
11→  containerHeight: number,
12→): { width: number; height: number } | undefined {
13→  return useMemo(() => {
14→    if (!sourceSize) return undefined;
15→    const scale = Math.max(
16→      containerWidth / sourceSize.width,
17→      containerHeight / sourceSize.height,
18→    );
19→    return {
20→      width: Math.round(sourceSize.width * scale),
21→      height: Math.round(sourceSize.height * scale),
22→    };
23→  }, [sourceSize, containerWidth, containerHeight]);
24→}
