 1→import { type JSX, useCallback, useRef } from "react";
 2→import { type ViewStyle } from "react-native";
 3→import {
 4→  ResumableZoom,
 5→  type ResumableZoomRefType,
 6→} from "react-native-zoom-toolkit";
 7→
 8→export function ZoomableImage({
 9→  children,
10→  style,
11→  onZoomChange,
12→  minScale = 1,
13→  maxScale = 5,
14→}: {
15→  children: JSX.Element;
16→  style?: ViewStyle;
17→  onZoomChange?: (isZoomed: boolean) => void;
18→  minScale?: number;
19→  maxScale?: number;
20→}): JSX.Element {
21→  const ref = useRef<ResumableZoomRefType>(null);
22→
23→  const handlePinchStart = useCallback((): void => {
24→    onZoomChange?.(true);
25→  }, [onZoomChange]);
26→
27→  const handlePanStart = useCallback((): void => {
28→    const state = ref.current?.getState();
29→    if (state && state.scale > minScale) {
30→      onZoomChange?.(true);
31→    }
32→  }, [onZoomChange, minScale]);
33→
34→  const handleGestureEnd = useCallback((): void => {
35→    const state = ref.current?.getState();
36→    if (!state) return;
37→    const atRest = Math.abs(state.scale - minScale) < 0.01;
38→    onZoomChange?.(!atRest);
39→  }, [onZoomChange, minScale]);
40→
41→  return (
42→    <ResumableZoom
43→      ref={ref}
44→      minScale={minScale}
45→      maxScale={maxScale}
46→      style={{ ...style, overflow: "hidden" }}
47→      onPinchStart={handlePinchStart}
48→      onPanStart={handlePanStart}
49→      onGestureEnd={handleGestureEnd}
50→    >
51→      {children}
52→    </ResumableZoom>
53→  );
54→}
