 1→import {
 2→  type ImageContentFit,
 3→  type ImageLoadEventData,
 4→  Image,
 5→} from "expo-image";
 6→import { type JSX, useCallback, useEffect } from "react";
 7→import { StyleSheet, View, type ViewStyle } from "react-native";
 8→import Animated, {
 9→  useAnimatedStyle,
10→  useSharedValue,
11→  withTiming,
12→} from "react-native-reanimated";
13→import type { ImageUri } from "@/lib/types/primitives";
14→
15→const AnimatedExpoImage = Animated.createAnimatedComponent(Image);
16→
17→const BLUR_RADIUS = 4;
18→const FADE_DURATION_MS = 500;
19→
20→export function ProgressiveImage({
21→  uri,
22→  thumbnailUri,
23→  style,
24→  contentFit = "cover",
25→  onLoad,
26→}: {
27→  uri: ImageUri;
28→  thumbnailUri: ImageUri | undefined;
29→  style?: ViewStyle;
30→  contentFit?: ImageContentFit;
31→  onLoad?: (event: ImageLoadEventData) => void;
32→}): JSX.Element {
33→  const thumbnailOpacity = useSharedValue(thumbnailUri ? 1 : 0);
34→
35→  useEffect(() => {
36→    if (thumbnailUri) {
37→      thumbnailOpacity.value = 1;
38→    }
39→  }, [thumbnailUri, thumbnailOpacity]);
40→
41→  const handleFullImageLoad = useCallback(
42→    (event: ImageLoadEventData): void => {
43→      thumbnailOpacity.value = withTiming(0, { duration: FADE_DURATION_MS });
44→      onLoad?.(event);
45→    },
46→    [thumbnailOpacity, onLoad],
47→  );
48→
49→  const thumbnailStyle = useAnimatedStyle(() => ({
50→    opacity: thumbnailOpacity.value,
51→  }));
52→
53→  return (
54→    <View style={[styles.container, style]}>
55→      <Image
56→        source={{ uri }}
57→        style={styles.image}
58→        contentFit={contentFit}
59→        onLoad={handleFullImageLoad}
60→      />
61→      {thumbnailUri && (
62→        <AnimatedExpoImage
63→          source={{ uri: thumbnailUri }}
64→          style={[styles.image, styles.thumbnailOverlay, thumbnailStyle]}
65→          contentFit={contentFit}
66→          blurRadius={BLUR_RADIUS}
67→        />
68→      )}
69→    </View>
70→  );
71→}
72→
73→const styles = StyleSheet.create({
74→  container: {
75→    overflow: "hidden",
76→  },
77→  image: {
78→    width: "100%",
79→    height: "100%",
80→  },
81→  thumbnailOverlay: {
82→    ...StyleSheet.absoluteFillObject,
83→  },
84→});
