  1→import spawnAsync from '@expo/spawn-async';
  2→import fs from 'fs';
  3→import { glob } from 'glob';
  4→import path from 'path';
  5→
  6→import { fileExistsAsync } from '../../fileUtils';
  7→import type {
  8→  AppleCodeSignEntitlements,
  9→  ExtraDependencies,
 10→  ModuleDescriptorIos,
 11→  ModuleIosPodspecInfo,
 12→  PackageRevision,
 13→} from '../../types';
 14→
 15→const APPLE_PROPERTIES_FILE = 'Podfile.properties.json';
 16→const APPLE_EXTRA_BUILD_DEPS_KEY = 'apple.extraPods';
 17→
 18→const indent = '  ';
 19→
 20→async function findPodspecFiles(revision: PackageRevision): Promise<string[]> {
 21→  const configPodspecPaths = revision.config?.applePodspecPaths();
 22→  if (configPodspecPaths && configPodspecPaths.length) {
 23→    return configPodspecPaths;
 24→  }
 25→
 26→  const podspecFiles = await glob('*/*.podspec', {
 27→    cwd: revision.path,
 28→    ignore: ['**/node_modules/**'],
 29→  });
 30→
 31→  return podspecFiles;
 32→}
 33→
 34→export function getSwiftModuleNames(
 35→  pods: ModuleIosPodspecInfo[],
 36→  swiftModuleNames: string[] | undefined
 37→): string[] {
 38→  if (swiftModuleNames && swiftModuleNames.length) {
 39→    return swiftModuleNames;
 40→  }
 41→  // by default, non-alphanumeric characters in the pod name are replaced by _ in the module name
 42→  return pods.map((pod) => pod.podName.replace(/[^a-zA-Z0-9]/g, '_'));
 43→}
 44→
 45→/** Resolves module search result with additional details required for iOS platform. */
 46→export async function resolveModuleAsync(
 47→  packageName: string,
 48→  revision: PackageRevision,
 49→  extraOutput: { flags?: Record<string, any> }
 50→): Promise<ModuleDescriptorIos | null> {
 51→  const podspecFiles = await findPodspecFiles(revision);
 52→  if (!podspecFiles.length) {
 53→    return null;
 54→  }
 55→
 56→  const pods = podspecFiles.map((podspecFile) => ({
 57→    podName: path.basename(podspecFile, path.extname(podspecFile)),
 58→    podspecDir: path.dirname(path.join(revision.path, podspecFile)),
 59→  }));
 60→
 61→  const swiftModuleNames = getSwiftModuleNames(pods, revision.config?.appleSwiftModuleNames());
 62→  const coreFeatures = revision.config?.coreFeatures() ?? [];
 63→
 64→  return {
 65→    packageName,
 66→    pods,
 67→    swiftModuleNames,
 68→    flags: extraOutput.flags,
 69→    modules: revision.config?.appleModules() ?? [],
 70→    appDelegateSubscribers: revision.config?.appleAppDelegateSubscribers() ?? [],
 71→    reactDelegateHandlers: revision.config?.appleReactDelegateHandlers() ?? [],
 72→    debugOnly: revision.config?.appleDebugOnly() ?? false,
 73→    ...(coreFeatures.length > 0 ? { coreFeatures } : {}),
 74→  };
 75→}
 76→
 77→export async function resolveExtraBuildDependenciesAsync(
 78→  projectNativeRoot: string
 79→): Promise<ExtraDependencies | null> {
 80→  const propsFile = path.join(projectNativeRoot, APPLE_PROPERTIES_FILE);
 81→  try {
 82→    const contents = await fs.promises.readFile(propsFile, 'utf8');
 83→    const podfileJson = JSON.parse(contents);
 84→    if (podfileJson[APPLE_EXTRA_BUILD_DEPS_KEY]) {
 85→      // expo-build-properties would serialize the extraPods as JSON string, we should parse it again.
 86→      const extraPods = JSON.parse(podfileJson[APPLE_EXTRA_BUILD_DEPS_KEY]);
 87→      return extraPods;
 88→    }
 89→  } catch {}
 90→  return null;
 91→}
