 93→/**
 94→ * Generates Swift file that contains all autolinked Swift packages.
 95→ */
 96→export async function generateModulesProviderAsync(
 97→  modules: ModuleDescriptorIos[],
 98→  targetPath: string,
 99→  entitlementPath: string | null
100→): Promise<void> {
101→  const className = path.basename(targetPath, path.extname(targetPath));
102→  const entitlements = await parseEntitlementsAsync(entitlementPath);
103→  const generatedFileContent = await generatePackageListFileContentAsync(
104→    modules,
105→    className,
106→    entitlements
107→  );
108→  const parentPath = path.dirname(targetPath);
109→  await fs.promises.mkdir(parentPath, { recursive: true });
110→  await fs.promises.writeFile(targetPath, generatedFileContent, 'utf8');
111→}
