 1→import { useBackgroundProcessing } from "@/features/background-processing";
 2→import { AddRecipeForm } from "@/features/recipe-form/components/AddRecipeForm";
 3→import { useRecipes } from "@/features/recipes-list/context/RecipesContext";
 4→import { sourceHistoryRepository } from "@/lib/repositories/sourceHistory";
 5→import type { PhotoUri } from "@/lib/types/primitives";
 6→import { useTheme, type Theme } from "@/platform/theme/useTheme";
 7→import { router, useLocalSearchParams } from "expo-router";
 8→import { useCallback, useState, type JSX } from "react";
 9→import { StyleSheet } from "react-native";
10→import { SafeAreaView } from "react-native-safe-area-context";
11→
12→export default function AddRecipeScreen(): JSX.Element {
13→  const theme = useTheme();
14→  const { uri } = useLocalSearchParams<{ uri: PhotoUri }>();
15→  const [source, setSource] = useState("");
16→  const { savePending } = useRecipes();
17→  const { addToQueue } = useBackgroundProcessing();
18→
19→  const handleSourceChange = useCallback(
20→    (newSource: string) => {
21→      setSource(newSource);
22→    },
23→    []
24→  );
25→
26→  const handleConfirm = useCallback(
27→    async (sourceOverride?: string) => {
28→      const effectiveSource = sourceOverride ?? source;
29→      if (!uri || !effectiveSource) {
30→        return;
31→      }
32→
33→      const recipe = await savePending(uri, effectiveSource);
34→      await sourceHistoryRepository.addSource(effectiveSource);
35→      addToQueue(recipe.id);
36→      router.replace("/");
37→    },
38→    [uri, source, savePending, addToQueue]
39→  );
40→
41→  if (!uri) {
42→    return (
43→      <SafeAreaView
44→        edges={["top", "left", "right"]}
45→        style={[styles.container, getThemeColors(theme).container]}
46→      />
47→    );
48→  }
49→
50→  return (
51→    <SafeAreaView
52→      edges={["top", "left", "right"]}
53→      style={[styles.container, getThemeColors(theme).container]}
54→    >
55→      <AddRecipeForm
56→        photoUri={uri}
57→        source={source}
58→        onSourceChange={handleSourceChange}
59→        onConfirm={handleConfirm}
60→      />
61→    </SafeAreaView>
62→  );
63→}
64→
65→function getThemeColors(theme: Theme) {
66→  const isDark = theme === "dark";
67→
68→  return {
69→    container: {
70→      backgroundColor: isDark ? "#000000" : "#FFFFFF",
71→    },
72→  };
73→}
74→
75→const styles = StyleSheet.create({
76→  container: {
77→    flex: 1,
78→  },
79→});
80→

